import type { Preview } from '@storybook/nextjs-vite';
import { SitecoreProvider } from '@sitecore-content-sdk/nextjs';
import { mockPageData } from './mockData/mockPageData';
import { mockApiData } from './mockData/mockApiData';
import mockComponentMap from './mockData/mockComponentMap';
import { I18nProvider } from 'next-localization';

import '../src/assets/main.css';

const preview: Preview = {
  parameters: {
    controls: {
      matchers: {
        color: /(background|color)$/i,
        date: /Date$/i,
      },
    },

    a11y: {
      // 'todo' - show a11y violations in the test UI only
      // 'error' - fail CI on a11y violations
      // 'off' - skip a11y checks entirely
      test: 'todo',
    },
    layout: 'fullscreen',
  },

  decorators: [
    (Story) => (
      <SitecoreProvider componentMap={mockComponentMap} page={mockPageData} api={mockApiData}>
        <I18nProvider locale="en" lngDict={{}}>
          <Story />
        </I18nProvider>
      </SitecoreProvider>
    ),
  ],

  argTypes: {
    fields: { table: { disable: true }, control: false },
    params: { table: { disable: true }, control: false },
    rendering: { table: { disable: true }, control: false },
  },
};

export default preview;

import { useEffect, JSX } from 'react';
import { CloudSDK } from '@sitecore-cloudsdk/core/browser';
import { SitecorePageProps } from '@sitecore-content-sdk/nextjs';
import '@sitecore-cloudsdk/events/browser';
import config from 'sitecore.config';

/**
 * The Bootstrap component is the entry point for performing any initialization logic
 * that needs to happen early in the application's lifecycle.
 */
const Bootstrap = (props: SitecorePageProps): JSX.Element | null => {
  const { page } = props;

  // Browser ClientSDK init allows for page view events to be tracked

  useEffect(() => {
    if (!page) {
      return;
    }

    const mode = page.mode;
    if (process.env.NODE_ENV === 'development')
      console.debug('Browser Events SDK is not initialized in development environment');
    else if (!mode.isNormal)
      console.debug('Browser Events SDK is not initialized in edit and preview modes');
    else {
      if (config.api.edge?.clientContextId) {
        CloudSDK({
          sitecoreEdgeUrl: config.api.edge.edgeUrl,
          sitecoreEdgeContextId: config.api.edge.clientContextId,
          siteName: page.siteName || config.defaultSite,
          enableBrowserCookie: true,
          // Replace with the top level cookie domain of the website that is being integrated e.g ".example.com" and not "www.example.com"
          cookieDomain: window.location.hostname.replace(/^www\./, ''),
        })
          .addEvents()
          .initialize();
      } else {
        console.error('Client Edge API settings missing from configuration');
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [page?.siteName]);

  return null;
};

export default Bootstrap;

'use client';
import { DesignLibrary } from '@sitecore-content-sdk/nextjs';

export const DesignLibraryLayout = () => {
  const importMapDynamic = () => import('.sitecore/import-map');
  return <DesignLibrary loadImportMap={importMapDynamic} />;
};

/**
 * This Layout is needed for Starter Kit.
 */
import React, { JSX } from 'react';
import Head from 'next/head';
import { Placeholder, Field, Page, ImageField } from '@sitecore-content-sdk/nextjs';
import Scripts from 'src/Scripts';
import SitecoreStyles from 'src/components/content-sdk/SitecoreStyles';
import { DesignLibraryLayout } from './DesignLibraryLayout';
import { useRouter } from 'next/router';

interface LayoutProps {
  page: Page;
}

interface RouteFields {
  [key: string]: unknown;
  Title?: Field;
  metadataTitle?: Field;
  metadataKeywords?: Field;
  metadataDescription?: Field;
  pageSummary?: Field;
  ogImage?: ImageField;
}

const Layout = ({ page }: LayoutProps): JSX.Element => {
  const router = useRouter();
  const { layout, mode } = page;
  const { route } = layout.sitecore;
  const fields = route?.fields as RouteFields;
  const mainClassPageEditing = mode.isEditing ? 'editing-mode' : 'prod-mode';

  const metaDescription =
    fields?.metadataDescription?.value?.toString() || fields?.pageSummary?.value?.toString() || '';
  const metaKeywords = fields?.metadataKeywords?.value?.toString() || '';
  const ogTitle = fields?.metadataTitle?.value?.toString() || 'Page';
  const ogImage = fields?.ogImage?.value?.src;
  const ogDescription =
    fields?.metadataDescription?.value?.toString() || fields?.pageSummary?.value?.toString() || '';
  const currentPath = router.asPath;
  const ogUrl = `${process.env.NEXT_PUBLIC_BASE_URL || ''}${currentPath}`;

  return (
    <>
      <Scripts />
      <SitecoreStyles layoutData={layout} />
      <Head>
        <title>{fields?.Title?.value?.toString() || 'Page'}</title>
        <link rel="icon" href="/favicon.ico" />
        {metaDescription && <meta name="description" content={metaDescription} />}
        {metaKeywords && <meta name="keywords" content={metaKeywords} />}
        <link rel="icon" href="/favicon.ico" />
        {ogTitle && <meta property="og:title" content={ogTitle} />}
        {ogDescription && <meta property="og:description " content={ogDescription} />}
        {ogImage && <meta property="og:image" content={ogImage} />}
        {ogUrl && <meta property="og:url" content={ogUrl} />}
        {/* Custom meta tag for current relative path - used in Search */}
        {currentPath && <meta name="page-path" content={currentPath} />}
      </Head>

      {/* root placeholder for the app, which we add components to using route data */}
      <div className={mainClassPageEditing}>
        {mode.isDesignLibrary ? (
          <DesignLibraryLayout />
        ) : (
          <>
            <header>
              <div id="header">
                {route && <Placeholder name="headless-header" rendering={route} />}
              </div>
            </header>
            <main>
              <div id="content">
                {route && <Placeholder name="headless-main" rendering={route} />}
              </div>
            </main>
            <footer>
              <div id="footer">
                {route && <Placeholder name="headless-footer" rendering={route} />}
              </div>
            </footer>
          </>
        )}
      </div>
    </>
  );
};

export default Layout;

import Head from 'next/head';
import { JSX } from 'react';

/**
 * Rendered in case if we have 404 error
 */
const NotFound = (): JSX.Element => (
  <>
    <Head>
      <title>404: NotFound</title>
    </Head>
    <div style={{ padding: 10 }}>
      <h1>Page not found</h1>
      <p>This page does not exist.</p>
      <a href="/">Go to the Home page</a>
    </div>
  </>
);

export default NotFound;

import { EditingScripts } from '@sitecore-content-sdk/nextjs';
// The BYOC bundle imports external (BYOC) components into the app and makes sure they are ready to be used
import BYOC from 'src/byoc';
import FEAASScripts from 'components/content-sdk/FEAASScripts';
import CdpPageView from 'components/content-sdk/CdpPageView';
import { JSX } from 'react';

const Scripts = (): JSX.Element => {
  return (
    <>
      <BYOC />
      <FEAASScripts />
      <CdpPageView />
      <EditingScripts />
    </>
  );
};

export default Scripts;

import * as FEAAS from '@sitecore-feaas/clientside/react';
/**
 * You can import your own client components here
 * @example
 * import './MyClientComponent';
 * @example
 * import 'src/otherFolder/MyOtherComponent';
 */

// An important boilerplate component that prevents BYOC components from being optimized away and allows then. Should be kept in this file.
const ClientsideComponent = (props: FEAAS.ExternalComponentProps) => FEAAS.ExternalComponent(props);
/**
 * Clientside BYOC component will be rendered in the browser, so that external components:
 * - Can have access to DOM apis, including network requests
 * - Use clientside react hooks like useEffect.
 * - Be implemented as web components.
 */

export default ClientsideComponent;

import React, { JSX } from 'react';
import * as FEAAS from '@sitecore-feaas/clientside/react';
import * as Events from '@sitecore-cloudsdk/events/browser';
import { LayoutServicePageState, SitecoreProviderReactContext } from '@sitecore-content-sdk/nextjs';
import '@sitecore/components/context';
import dynamic from 'next/dynamic';
import config from 'sitecore.config';
/**
 * This is an out-of-box bundler for External components (BYOC) (see Sitecore documentation for more details)
 * It enables registering components in client-only or SSR/hybrid contexts
 * It's recommended to not modify this file - please add BYOC imports in corresponding index.*.ts files instead
 */

// Import your client-only components via client-bundle. Nextjs's dynamic() call will ensure they are only rendered client-side
const ClientBundle = dynamic(() => import('./index.client'), {
  ssr: false,
});

// As long as component bundle is exported and rendered on page (as an empty element), client-only BYOC components are registered and become available
// The rest of components will be regsitered in both server and client-side contexts when this module is imported into Layout
FEAAS.enableNextClientsideComponents(dynamic, ClientBundle);

// Import your hybrid (server rendering with client hydration) components via index.hybrid.ts
import './index.hybrid';

const BYOCInit = (): JSX.Element | null => {
  const { page } = React.useContext(SitecoreProviderReactContext);
  const { pageState } = page.layout.sitecore.context;
  // Set context properties to be available within BYOC components
  FEAAS.setContextProperties({
    sitecoreEdgeUrl: config.api.edge?.edgeUrl,
    sitecoreEdgeContextId: config.api.edge?.contextId,
    pageState: pageState || LayoutServicePageState.Normal,
    siteName: page.siteName || config.defaultSite,
    eventsSDK: Events,
  });

  return <FEAAS.ExternalComponentBundle />;
};

export default BYOCInit;

import { ComponentProps } from '@/lib/component-props';
import {
  NextImage as ContentSdkImage,
  Text as ContentSdkText,
  RichText as ContentSdkRichText,
  Placeholder,
  useSitecore,
  DateField,
} from '@sitecore-content-sdk/nextjs';
import { Bookmark, Calendar, User } from 'lucide-react';
import { ParentPathLink } from '../non-sitecore/ParentPathLink';
import { useI18n } from 'next-localization';
import { useEffect, useState } from 'react';
import SocialShare from '../non-sitecore/SocialShare';
import { newsDateFormatter } from '@/helpers/dateHelper';
import { ArticleFields } from '@/types/article';

interface ArticleDetailsProps extends ComponentProps {
  fields: ArticleFields;
}

export const Default = ({ params, fields, rendering }: ArticleDetailsProps) => {
  const { page } = useSitecore();
  const [currentUrl, setCurrentUrl] = useState('');
  const { styles, RenderingIdentifier: id, DynamicPlaceholderId } = params;
  const fullWidthPlaceholderKey = `article-details-full-width-${DynamicPlaceholderId}`;
  const isPageEditing = page.mode.isEditing;
  const { t } = useI18n();

  useEffect(() => {
    if (typeof window !== 'undefined') {
      setCurrentUrl(window.location.href);
    }
  }, []);

  if (!fields?.Title) {
    return isPageEditing ? (
      <div className={`component article-details ${styles}`} id={id}>
        [ARTICLE DETAILS]
      </div>
    ) : (
      <></>
    );
  }

  return (
    <div className={`w-full space-y-6 py-6 ${styles}`} id={id}>
      <div className="container">
        <ParentPathLink text={t('back_to_articles') || 'Back to Articles'} />
      </div>

      <article className="container">
        <div className="mx-auto max-w-4xl space-y-6">
          <div className="flex flex-wrap items-center gap-6 text-sm">
            <ContentSdkText
              field={fields.Category?.fields?.Category}
              tag="span"
              className="border-accent text-accent-dark bg-background-accent rounded-md border px-2 py-0.5 font-medium"
            />

            {(fields?.PublishedDate?.value || isPageEditing) && (
              <div className="flex items-center gap-1">
                <User className="size-4" />
                <ContentSdkText field={fields?.Author?.fields?.AuthorName} tag="p" />
              </div>
            )}
            {(fields?.PublishedDate?.value || isPageEditing) && (
              <div className="flex items-center gap-1">
                <Calendar className="size-4" />
                <DateField
                  tag="p"
                  className="news-date"
                  field={fields?.PublishedDate}
                  render={newsDateFormatter}
                />
              </div>
            )}
          </div>

          <h1>
            <ContentSdkText field={fields?.Title} />
          </h1>

          <div className="flex gap-4">
            <SocialShare
              url={currentUrl}
              title={fields?.Title?.value || ''}
              description={fields?.ShortDescription?.value || ''}
              mediaUrl={fields?.Image?.value?.src || ''}
            />
            <button className="simple-btn">
              <Bookmark />
              {t('save_label') || 'Save'}
            </button>
          </div>

          <ContentSdkImage field={fields?.Image} className="h-auto w-full rounded-lg" />

          <div>
            <ContentSdkRichText field={fields?.Content} />
          </div>
        </div>
      </article>

      <Placeholder rendering={rendering} name={fullWidthPlaceholderKey} />

      {fields.Tags && fields.Tags.length > 0 && (
        <div className="container">
          <div className="mx-auto max-w-4xl">
            <div className="bg-background w-full space-y-4 rounded-lg border p-6 shadow-md">
              <h6>{t('tags_label') || 'Tags'}</h6>
              <div className="flex flex-wrap gap-4">
                {fields.Tags.map((tag) => (
                  <span key={tag.id} className="bg-background-muted rounded-sm px-2 py-0.5 text-sm">
                    <ContentSdkText field={tag.fields.Tag} />
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

import { getArticlesCountsByCategory } from '@/helpers/articleHelpers';
import { ComponentProps } from '@/lib/component-props';
import InfiniteScroll from '@/shadcn/components/ui/infiniteScroll';
import { ArticleFields } from '@/types/article';
import { NextImage as ContentSdkImage } from '@sitecore-content-sdk/nextjs';
import { Loader2 } from 'lucide-react';
import { useI18n } from 'next-localization';
import { useCallback, useEffect, useMemo, useState } from 'react';
import ArticleCard from '../non-sitecore/ArticleCard';

interface ArticleListingProps extends ComponentProps {
  params: { [key: string]: string };
  fields: {
    items: {
      id: string;
      url: string;
      fields: ArticleFields;
    }[];
  };
}

export const Default = (props: ArticleListingProps) => {
  const { t } = useI18n();
  const id = props.params.RenderingIdentifier;
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  const articles = props.fields?.items.filter(({ fields }) => fields && Object.keys(fields).length);
  const articlesCategoryCounts = getArticlesCountsByCategory(articles);

  const handleSelectAllArticles = () => {
    setSelectedCategory(null);
    setVisibleCount(articles?.length);
  };

  const filteredArticles = useMemo(() => {
    if (!selectedCategory) return articles;
    return articles.filter(
      (article) => article?.fields?.Category?.fields?.Category?.value === selectedCategory
    );
  }, [selectedCategory, articles]);

  const PAGE_SIZE = 6;
  const [visibleCount, setVisibleCount] = useState(PAGE_SIZE);
  const [isLoading, setIsLoading] = useState(false);

  const visibleArticles = filteredArticles.slice(0, visibleCount);
  const hasMore = visibleCount < filteredArticles.length;

  const loadMore = useCallback(() => {
    if (isLoading || !hasMore) return;
    setIsLoading(true);
    setTimeout(() => {
      setVisibleCount((prev) => prev + PAGE_SIZE);
      setIsLoading(false);
    }, 500);
  }, [hasMore, isLoading]);

  useEffect(() => {
    if (selectedCategory) {
      setVisibleCount(PAGE_SIZE);
    }
  }, [selectedCategory]);

  return (
    <section className={`${props?.params?.styles?.trimEnd()}`} id={id}>
      <div className="container py-5">
        <div className="py-10">
          <h5>{t('browse_by_category_label') || 'Browse by Category'}</h5>
          <div className="grid grid-cols-2 gap-4 py-5 md:grid-cols-3 lg:grid-cols-6">
            {articlesCategoryCounts.map((category, index) => (
              <div
                key={category.name + index}
                onClick={() => setSelectedCategory(category.name)}
                className={`bg-background flex aspect-square w-full cursor-pointer flex-col items-center justify-center rounded-lg border shadow-md transition-colors hover:shadow-lg ${selectedCategory === category.name ? 'border-accent pointer-events-none shadow-lg' : ''}`}
              >
                <div>
                  <ContentSdkImage
                    field={category.icon}
                    width={50}
                    height={50}
                    className="p-1"
                    priority
                  />
                </div>

                <div className="mt-2 text-center font-bold xl:mt-5">{category.name}</div>
                <div className="border-accent text-accent-dark bg-background-accent mt-2 rounded-md border px-2 text-sm font-medium">
                  {category.count} {t('articles_label') || 'articles'}
                </div>
              </div>
            ))}
          </div>
        </div>

        <div>
          <div className="flex justify-between">
            <h5>{t('latest_articles_label') || 'Latest Articles'}</h5>
            <button className="simple-btn" onClick={handleSelectAllArticles}>
              {t('view_all_articles_label') || 'View All Articles'}
            </button>
          </div>
          <div className="grid grid-cols-1 gap-5 py-5 md:grid-cols-2 lg:grid-cols-3">
            {visibleArticles?.map(({ fields, id, url }) => (
              <ArticleCard key={id} fields={fields} id={id} url={url} />
            ))}

            <InfiniteScroll isLoading={isLoading} hasMore={hasMore} next={loadMore} threshold={1}>
              {hasMore && (
                <div className="col-span-full flex justify-center py-4">
                  <Loader2 className="text-accent-dark h-8 w-8 animate-spin" />
                </div>
              )}
            </InfiniteScroll>
          </div>
        </div>
      </div>
    </section>
  );
};

import React, { JSX } from 'react';
import { Placeholder } from '@sitecore-content-sdk/nextjs';
import { ComponentProps } from 'lib/component-props';

/**
 * The number of columns that can be inserted into the column splitter component.
 * The maximum number of columns is 8.
 */
type ColumnNumber = 1 | 2 | 3 | 4 | 5 | 6 | 7 | 8;

/**
 * The width specified for each rendered column.
 * The key is the column number, and the value is the width.
 */
type ColumnWidths = {
  [K in ColumnNumber as `ColumnWidth${K}`]?: string;
};

/**
 * The styles specified for each rendered column.
 * The key is the column number, and the value is the styles.
 */
type ColumnStyles = {
  [K in ColumnNumber as `Styles${K}`]?: string;
};

interface ColumnSplitterProps extends ComponentProps {
  params: ComponentProps['params'] & ColumnWidths & ColumnStyles;
}

export const Default = ({ params, rendering }: ColumnSplitterProps): JSX.Element => {
  const { EnabledPlaceholders, RenderingIdentifier: id, styles } = params;

  const enabledColumns = EnabledPlaceholders?.split(',') ?? [];

  return (
    <div className={`row component column-splitter ${styles}`} id={id}>
      {enabledColumns.map((columnNum, index) => {
        const num = Number(columnNum) as ColumnNumber;
        const columnWidth = params[`ColumnWidth${num}`] ?? '';
        const columnStyle = params[`Styles${num}`] ?? '';
        const columnClassNames = `${columnWidth} ${columnStyle}`.trim();

        return (
          <div key={index} className={columnClassNames}>
            <div className="row">
              <Placeholder name={`column-${columnNum}-{*}`} rendering={rendering} />
            </div>
          </div>
        );
      })}
    </div>
  );
};

import { Placeholder } from '@sitecore-content-sdk/nextjs';
import React, { JSX } from 'react';
import { extractMediaUrl } from '@/helpers/extractMediaUrl';
import { ComponentProps } from 'lib/component-props';

interface ContainerProps extends ComponentProps {
  params: ComponentProps['params'] & {
    BackgroundImage?: string;
    DynamicPlaceholderId: string;
  };
}

const Container = ({ params, rendering }: ContainerProps): JSX.Element => {
  const {
    styles,
    RenderingIdentifier: id,
    BackgroundImage: backgroundImage,
    DynamicPlaceholderId,
  } = params;
  const phKey = `container-${DynamicPlaceholderId}`;

  const mediaUrl = extractMediaUrl(backgroundImage);
  let backgroundStyle: { [key: string]: string } = {};
  if (mediaUrl) {
    backgroundStyle = {
      backgroundImage: `url('${mediaUrl}')`,
    };
  }

  return (
    <div className={`component container-default ${styles}`} id={id}>
      <div className="component-content" style={backgroundStyle}>
        <div className="row">
          <Placeholder name={phKey} rendering={rendering} />
        </div>
      </div>
    </div>
  );
};

export const Default = (props: ContainerProps): JSX.Element => {
  const styles = props.params?.styles?.split(' ');

  return styles?.includes('container') ? (
    <div className="container-wrapper">
      <Container {...props} />
    </div>
  ) : (
    <Container {...props} />
  );
};

import { Text, RichText, Field, withDatasourceCheck } from '@sitecore-content-sdk/nextjs';
import { ComponentProps } from 'lib/component-props';
import { JSX } from 'react';

type ContentBlockProps = ComponentProps & {
  fields: {
    heading: Field<string>;
    content: Field<string>;
  };
};

/**
 * A simple Content Block component, with a heading and rich text block.
 * This is the most basic building block of a content site, and the most basic
 * Content SDK component that's useful.
 */
const ContentBlock = ({ fields }: ContentBlockProps): JSX.Element => (
  <div className="contentBlock">
    <Text tag="h2" className="contentTitle" field={fields.heading} />

    <RichText className="contentDescription" field={fields.content} />
  </div>
);

export default withDatasourceCheck()<ContentBlockProps>(ContentBlock);

import { CdpHelper, useSitecore } from '@sitecore-content-sdk/nextjs';
import { useEffect } from 'react';
import { pageView } from '@sitecore-cloudsdk/events/browser';
import config from 'sitecore.config';
import { JSX } from 'react';

/**
 * This is the CDP page view component.
 * It uses the Sitecore Cloud SDK to enable page view events on the client-side.
 * See Sitecore Cloud SDK documentation for details.
 * https://www.npmjs.com/package/@sitecore-cloudsdk/events
 */
const CdpPageView = (): JSX.Element => {
  const {
    page: { layout, siteName, mode },
  } = useSitecore();
  const { route, context } = layout.sitecore;

  /**
   * Determines if the page view events should be turned off.
   * IMPORTANT: You should implement based on your cookie consent management solution of choice.
   * By default it is disabled in development mode
   */
  const disabled = () => {
    return process.env.NODE_ENV === 'development';
  };

  useEffect(() => {
    // Do not create events in editing or preview mode or if missing route data
    if (!mode.isNormal || !route?.itemId) {
      return;
    }
    // Do not create events if disabled (e.g. we don't have consent)
    if (disabled()) {
      return;
    }

    const language = route.itemLanguage || config.defaultLanguage;
    const scope = config.personalize?.scope;

    const pageVariantId = CdpHelper.getPageVariantId(
      route.itemId,
      language,
      context.variantId as string,
      scope
    );
    // there can be cases where Events are not initialized which are expected to reject
    pageView({
      channel: 'WEB',
      currency: 'USD',
      page: route.name,
      pageVariantId,
      language,
    }).catch((e) => console.debug(e));
  }, [mode, route, context.variantId, siteName]);

  return <></>;
};

export default CdpPageView;

import Image from 'next/image';
import * as FEAAS from '@sitecore-feaas/clientside/react';
import nextConfig from 'next.config';
import { JSX } from 'react';
// Element implementations for Sitecore Component Builder can be overriden here

const FEAASScripts = (): JSX.Element => {
  // we cannot use nextjs's logic for remotePatterns matching without extra dependencies
  // so we use a limited approach for now - which will be replaced once nextjs allows to fall back to unoptimized OOB
  const convertToRegex = (pattern: string) => {
    return pattern.replace('.', '\\.').replace(/\*/g, '.*');
  };

  const shouldOptimize = (src: string) => {
    if (src.startsWith('http')) {
      const url = new URL(src);
      const domains: string[] = nextConfig.images?.domains || [];
      const remotePatterns = nextConfig.images?.remotePatterns || [];
      return (
        domains.some((domain) => url.hostname === domain) ||
        remotePatterns.some(
          (pattern) =>
            pattern.protocol === url.protocol.slice(0, -1) &&
            new RegExp('^' + convertToRegex(pattern.hostname) + '$').test(url.hostname)
        )
      );
    }
    return true;
  };

  // Register next Image to be used in Component Builder.
  // Nextjs image implementation will be used when img is rendered in component from Component Builder
  FEAAS.setElementImplementation('img', (attributes: Record<string, string>) => {
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const { children, src, alt, ...imgAttributes } = attributes;
    return (
      <Image
        height={1920}
        width={1200}
        unoptimized={!shouldOptimize(src)}
        src={src}
        alt={alt}
        {...imgAttributes}
      />
    );
  });

  return <></>;
};

export default FEAASScripts;

import Head from 'next/head';
import client from 'lib/sitecore-client';
import { LayoutServiceData, HTMLLink } from '@sitecore-content-sdk/nextjs';

/**
 * Component to render `<link>` elements for Sitecore styles
 */
const SitecoreStyles = ({
  layoutData,
  enableStyles,
  enableThemes,
}: {
  layoutData: LayoutServiceData;
  enableStyles?: boolean;
  enableThemes?: boolean;
}) => {
  const headLinks = client.getHeadLinks(layoutData, { enableStyles, enableThemes });

  if (headLinks.length === 0) {
    return null;
  }

  return (
    <Head>
      {headLinks.map(({ rel, href }: HTMLLink) => (
        <link rel={rel} key={href} href={href} />
      ))}
    </Head>
  );
};

export default SitecoreStyles;

