        <span>
          {t('grid_operating_at') || 'Operating at'} {operatingCapacity.toFixed(1)}%{' '}
          {(t('grid_capacity_label') || 'Capacity').toLowerCase()}.{' '}
          {t('grid_reserve_margin') || 'Reserve margin'}: {GRID_CONDITIONS_DATA.grid.margin.value}%
        </span>
      </div>
    </>
  );
};

