  title,
  description = '',
  mediaUrl = '',
  round = false,
  className = '',
  iconClassName = '',
  platforms = ['facebook', 'twitter', 'linkedin', 'pinterest', 'email'],
}: SocialShareProps) => {
  const { t } = useI18n();
  const iconClass = `size-7 md:size-8 rounded-sm ${iconClassName || ''}`;

  const renderSocialButton = (platform: SocialPlatform) => {
    switch (platform) {
      case 'facebook':
        return (
          <FacebookShareButton
