      case 'twitter':
        return (
          <TwitterShareButton
            url={currentUrl}
            title={title}
            aria-label={`Share "${title}" on Twitter`}
            key={platform}
          >
            <TwitterIcon className={iconClass} round={round} />
          </TwitterShareButton>
        );
      case 'linkedin':
        return (
          <LinkedinShareButton
            url={currentUrl}
            title={title}
            summary={description}
            aria-label={`Share "${title}" on LinkedIn`}
            key={platform}
          >
            <LinkedinIcon className={iconClass} round={round} />
          </LinkedinShareButton>
        );
      case 'pinterest':
        return (
          <PinterestShareButton
            url={currentUrl}
            media={mediaUrl}
            aria-label={`Share "${title}" on Pinterest`}
            key={platform}
          >
            <PinterestIcon className={iconClass} round={round} />
          </PinterestShareButton>
        );
      case 'email':
        return (
          <EmailShareButton
            url={currentUrl}
            subject={title}
            body={description}
            aria-label={`Share "${title}" via Email`}
            key={platform}
          >
            <EmailIcon className={iconClass} round={round} />
          </EmailShareButton>
        );
      default:
        return null;
    }
  };

  return (
    <div className={className}>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <button className="simple-btn" aria-label="Share">
            <Share2 /> {t('share_label') || 'Share'}
          </button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="min-w-none">
          {platforms.map((platform) => (
            <DropdownMenuItem key={platform}>{renderSocialButton(platform)}</DropdownMenuItem>
          ))}
        </DropdownMenuContent>
      </DropdownMenu>
    </div>
  );
};

