import { useState } from 'react';
import { GridIcon, ListBulletIcon } from '@radix-ui/react-icons';
import type { SearchResultsInitialState, SearchResultsStoreState } from '@sitecore-search/react';
import { WidgetDataType, useSearchResults, widget } from '@sitecore-search/react';
import React from 'react';
import HomeHighlighted from './HomeHighlighted';
import Spinner from './Spinner';
import ArticleItemCard from './ArticleCard';
import SortOrder from './SortOrder';
import ArticleHorizontalItemCard from './ArticleHorizontalCard';
import SearchPagination from './SearchPagination';
import SearchFacets from './SearchFacets';
import ResultsPerPage from './ResultsPerPage';
import QueryResultsSummary from './QueryResultsSummary';
import CardViewSwitcher from './CardViewSwitcher';
import { HIGHLIGHTED_ARTICLES_RFKID, SEARCH_WIDGET_ID } from '@/constants/search';
import { useSearchTracking, type Events } from '@/hooks/useSearchTracking';

const SEARCH_CONFIG = {
  source: process.env.NEXT_PUBLIC_GRIDWELL_SEARCH_SOURCE as string,
};

export type ArticleModel = {
  id: string;
  type?: string;
  title?: string;
  name?: string;
  subtitle?: string;
  url: string;
  description?: string;
  content_text?: string;
  image_url?: string;
  source_id?: string;
};

type ArticleSearchResultsProps = {
  defaultSortType?: SearchResultsStoreState['sortType'];
  defaultPage?: SearchResultsStoreState['page'];
  defaultItemsPerPage?: SearchResultsStoreState['itemsPerPage'];
  defaultKeyphrase?: SearchResultsStoreState['keyphrase'];
};

type InitialState = SearchResultsInitialState<'itemsPerPage' | 'keyphrase' | 'page' | 'sortType'>;

export const SearchResultsComponent = ({
  defaultSortType = 'featured_desc',
  defaultPage = 1,
  defaultKeyphrase = '',
  defaultItemsPerPage = 10,
}: ArticleSearchResultsProps) => {
  const {
    state: { sortType, page, itemsPerPage },
    queryResult: {
      isLoading,
      isFetching,
      data: {
        total_item: totalItems = 0,
        sort: { choices: sortChoices = [] } = {},
        facet: facets = [],
        content: articles = [],
      } = {},
    },
  } = useSearchResults<ArticleModel, InitialState>({
    state: {
      sortType: defaultSortType,
      page: defaultPage,
      itemsPerPage: defaultItemsPerPage,
      keyphrase: defaultKeyphrase,
    },
    query: (query) => {
      if (SEARCH_CONFIG.source !== '') {
        const sources = SEARCH_CONFIG.source.split('|');
        sources.forEach((source) => {
          query.getRequest().addSource(source.trim());
        });
      }
    },
  });

  const totalPages = Math.ceil(totalItems / itemsPerPage);
  const defaultCardView = 'list';
  const [dir, setDir] = useState(defaultCardView);
  const onToggle = (value = defaultCardView) => setDir(value);

  //  Call the hook at the top level of the component
  const { handleSearch } = useSearchTracking();

  if (isLoading) {
    return (
      <div className="flex h-screen w-full items-center justify-center">
        <Spinner loading />
      </div>
    );
  }

  return (
    <div className="relative flex w-full px-4">
      {isFetching && (
        <div className="bg-background fixed top-0 right-0 bottom-0 left-0 z-30 h-full w-full opacity-50">
          <div className="absolute top-1/2 left-1/2 z-40 flex -translate-x-1/2 -translate-y-1/2 flex-col items-center justify-center">
            <Spinner loading />
          </div>
        </div>
      )}

      {totalItems > 0 && (
        <div key="1" className="mt-4 flex w-full flex-col md:flex-row md:space-x-8">
          <section className="relative md:w-1/4">
            <SearchFacets facets={facets} />
          </section>

          <section className="flex flex-1 flex-col md:w-3/4">
            {/* Sort / Controls */}
            <section className="bg-background-accent flex w-full justify-between rounded-sm">
              <div className="container flex flex-col justify-between gap-5 py-5 sm:flex-row sm:items-center">
                {totalItems > 0 && (
                  <QueryResultsSummary
                    currentPage={page}
                    itemsPerPage={itemsPerPage}
                    totalItems={totalItems}
                    totalItemsReturned={articles.length}
                  />
                )}
                <div className="gap flex items-center gap-x-7">
                  <ResultsPerPage defaultItemsPerPage={defaultItemsPerPage} />
                </div>
                <div className="flex flex-col gap-3 sm:flex-row sm:items-center">
                  <CardViewSwitcher
                    onToggle={onToggle}
                    defaultCardView={defaultCardView}
                    GridIcon={GridIcon}
                    ListIcon={ListBulletIcon}
                  />
                  <SortOrder options={sortChoices} selected={sortType} />
                </div>
              </div>
            </section>

            {/* Results */}
            {dir === 'grid' ? (
              <div className="mt-4 grid grid-cols-2 gap-x-3 gap-y-3 sm:grid-cols-3 md:grid-cols-3 md:gap-x-5 lg:grid-cols-4 xl:gap-x-6 xl:gap-y-4">
                {articles.map((a, index) => (
                  <ArticleItemCard
                    key={a.id}
                    article={a}
                    index={index}
                    onItemClick={(e) =>
                      handleSearch(e, {
                        url: a.url,
                        widgetId: SEARCH_WIDGET_ID,
                        entityType: 'content',
                        events: ['EntityPageView', 'SearchClickEvent'] as Events[],
                        entityId: a.id,
                        itemIndex: index,
                      })
                    }
                  />
                ))}
              </div>
            ) : (
              <div className="w-full">
                {articles.map((a, index) => (
                  <ArticleHorizontalItemCard
                    key={a.id}
                    article={a as ArticleModel}
                    index={index}
                    onItemClick={(e) =>
                      handleSearch(e, {
                        url: a.url,
                        widgetId: SEARCH_WIDGET_ID,
                        entityType: 'content',
                        events: ['EntityPageView', 'SearchClickEvent'] as Events[],
                        entityId: a.id,
                        itemIndex: index,
                      })
                    }
                    displayText={true}
                  />
                ))}
              </div>
            )}

            <div className="mx-auto my-8">
              <SearchPagination currentPage={page} totalPages={totalPages} />
            </div>
          </section>
        </div>
      )}

      {totalItems <= 0 && !isFetching && <HomeHighlighted rfkId={HIGHLIGHTED_ARTICLES_RFKID} />}
    </div>
  );
};

const SearchResultsWidget = widget(
  SearchResultsComponent,
  WidgetDataType.SEARCH_RESULTS,
  'content'
);
export default SearchResultsWidget;

import type { SearchResponseSortChoice } from '@sitecore-search/react';
import { useSearchResultsActions } from '@sitecore-search/react';
import { SortSelect } from '@sitecore-search/ui';

type SortOrderProps = {
  options: Array<SearchResponseSortChoice>;
  selected: string;
};
const SortOrder = ({ options, selected }: SortOrderProps) => {
  const selectedSortIndex = options.findIndex((s) => s.name === selected);
  const { onSortChange } = useSearchResultsActions();
  return (
    <SortSelect.Root defaultValue={options[selectedSortIndex]?.name} onValueChange={onSortChange}>
      <SortSelect.Trigger className="focus:outline-accent inline-flex h-10 cursor-pointer items-center gap-1 border-0 bg-transparent py-1">
        <SortSelect.SelectValue>
          {selectedSortIndex > -1 ? options[selectedSortIndex].label : ''}
        </SortSelect.SelectValue>
        <SortSelect.Icon />
      </SortSelect.Trigger>
      <SortSelect.Content className="bg-background absolute top-8 z-100 min-w-37.5 rounded-md shadow-sm">
        <SortSelect.Viewport className="z-50000 p-1">
          {options.map((option) => (
            <SortSelect.Option
              value={option}
              key={option.name}
              className="text-foreground-muted whitespace-no-wrap hover:text-foreground focus:outline-accent data-[state=checked]:bg-background-accent data-[state=checked]:text-foreground flex h-6 cursor-pointer items-center rounded-sm px-1 leading-none select-none"
            >
              <SortSelect.OptionText>{option.label}</SortSelect.OptionText>
            </SortSelect.Option>
          ))}
        </SortSelect.Viewport>
      </SortSelect.Content>
    </SortSelect.Root>
  );
};

export default SortOrder;

import { LoaderCircle } from 'lucide-react';

type SpinnerProps = {
  loading?: boolean;
};

const Spinner = ({ loading = false }: SpinnerProps) => {
  if (loading) {
    return (
      <div className="absolute top-1/2 right-0 left-0 block h-full w-full items-center text-center">
        <div role="status">
          <LoaderCircle
