      )}
      <Text tag="span" className="image-caption" field={fields.ImageCaption} />
    </ImageWrapper>
  );
};

'use client';

import React, { useMemo, useCallback } from 'react';
import { useRouter } from 'next/router';
import { Check, Globe, X } from 'lucide-react';
import { ComponentProps } from '@/lib/component-props';
import { useSitecore } from '@sitecore-content-sdk/nextjs';
import { AppLocale } from '@/types/locale';
import { localeOptions } from '@/constants/localeOptions';
import { DrawerClose, DrawerContent, DrawerTrigger, Drawer } from '@/shadcn/components/ui/drawer';

export type LanguageSwitcherProps = ComponentProps & {
  params: { [key: string]: string };
};

export default function LanguageSwitcher(props: LanguageSwitcherProps) {
  const { styles, RenderingIdentifier: id } = props.params;

  const router = useRouter();
  const { pathname, asPath, query } = router;

  const { page } = useSitecore();
  const activeLocale = useMemo<AppLocale>(() => page?.locale as AppLocale, [page?.locale]);

  const changeLanguage = useCallback(
    (langCode: AppLocale) => {
      if (pathname && asPath && query) {
        router.push(
          {
            pathname,
            query,
          },
          asPath,
          {
            locale: langCode,
            shallow: false,
          }
        );
      }
    },
    [asPath, pathname, query, router]
  );

  const selectedLocale: AppLocale = localeOptions.some((l) => l.code === activeLocale)
    ? activeLocale
    : 'en';

  const selectedLocaleLabel =
    localeOptions.find((l) => l.code === selectedLocale)?.label || selectedLocale.toUpperCase();

  return (
    <div className={`component language-switcher ${styles}`} id={id}>
      <Drawer direction="right">
        <DrawerTrigger asChild>
          <button
            type="button"
            aria-label={`Current Language: ${selectedLocale}`}
            className="text-foreground hover:text-foreground-light flex items-center gap-2 border-0 bg-transparent p-2 transition-colors [.component.header_&]:px-1"
          >
            <Globe className="size-5" />
            <span className="uppercase max-lg:hidden">{selectedLocaleLabel}</span>
          </button>
        </DrawerTrigger>

        <DrawerContent
          data-slot="drawer-content"
          className="bg-background-muted ml-auto min-h-full p-6"
        >
          <DrawerClose asChild className="self-end">
            <button
              type="button"
              aria-label="Close"
              className="text-foreground hover:text-foreground-light"
            >
              <X className="size-5" />
            </button>
          </DrawerClose>
          <h4 className="drawer-heading">Select Language</h4>

          <ul className="flex flex-col gap-3">
            {localeOptions.map((language) => (
              <li key={language.code} className="flex items-center gap-4">
                <button
                  onClick={() => changeLanguage(language.code as AppLocale)}
                  className={
                    'text-foreground hover:text-foreground-light py-2 text-left text-xl transition-colors'
                  }
                >
                  {language.label}
                </button>
                {language.code === selectedLocale && <Check className="text-accent size-6" />}
              </li>
            ))}
          </ul>
        </DrawerContent>
      </Drawer>
    </div>
  );
}

import React from 'react';
import { Link as ContentSdkLink, Text, LinkField, TextField } from '@sitecore-content-sdk/nextjs';
import { ComponentProps } from 'lib/component-props';

interface LinkListProps extends ComponentProps {
  fields: {
    /**
     * The Integrated graphQL query result. This illustrates the way to access the datasource children.
     */
    data: {
      datasource: {
        children: {
          results: Array<{
            field: {
              link: LinkField;
            };
          }>;
        };
        field: {
          title: TextField;
        };
      };
    };
  };
}

const LinkListItem = ({
  index,
  total,
  field,
  className,
}: {
  index: number;
  total: number;
  field: LinkField;
  className?: string;
}) => {
  const classNames = [
    `item${index}`,
    index % 2 === 0 ? 'odd' : 'even',
    index === 0 ? 'first' : '',
    index === total - 1 ? 'last' : '',
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <li className={classNames}>
      <div className={`field-link`}>
        <ContentSdkLink field={field} className={className} />
      </div>
    </li>
  );
};

export const Default = ({ params, fields }: LinkListProps) => {
  const datasource = fields?.data?.datasource;
  const styles = `component link-list ${params.styles || ''}`.trim();
  const id = params.RenderingIdentifier;

  const renderContent = () => {
    if (!datasource) {
      return <h3>Link List</h3>;
    }

    const links = datasource.children.results
      .filter((element) => element?.field?.link)
      .map((element, index) => (
        <LinkListItem
          key={`${index}-${element.field?.link}`}
          index={index}
          total={datasource.children.results.length}
          field={element.field.link}
        />
      ));

    return (
      <>
        <Text tag="h3" field={datasource.field?.title} />
        <ul>{links}</ul>
      </>
    );
  };

  return (
    <div className={styles} id={id}>
      <div className="component-content">{renderContent()}</div>
    </div>
  );
};

export const SecondaryNavigation = ({ params, fields }: LinkListProps) => {
  const datasource = fields?.data?.datasource;
  const styles = `component link-list ${params.styles || ''}`.trim();
  const id = params.RenderingIdentifier;

  const renderContent = () => {
    if (!datasource) {
      return <h3>Link List</h3>;
    }

    const links = datasource.children.results
      .filter((element) => element?.field?.link)
      .map((element, index) => (
        <LinkListItem
          key={`${index}-${element.field?.link}`}
          index={index}
          total={datasource.children.results.length}
          field={element.field.link}
          className="navigation-item"
        />
      ));

    return <ul className="flex gap-x-6 gap-y-4 text-xs [.drawer-content_&]:!flex-col">{links}</ul>;
  };

  return (
    <div className={styles} id={id}>
      <div className="component-content">{renderContent()}</div>
    </div>
  );
};

