import {
  Field,
  ImageField,
  RichTextField,
  NextImage as ContentSdkImage,
  Text as ContentSdkText,
  DateField,
  Placeholder,
  useSitecore,
} from '@sitecore-content-sdk/nextjs';
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCalendar, faTag } from '@fortawesome/free-solid-svg-icons';
import { IconProp } from '@fortawesome/fontawesome-svg-core';
import { ComponentProps } from '@/lib/component-props';
import Link from 'next/link';
import { useI18n } from 'next-localization';
import { sortByDateDesc, getCategoryCounts } from '@/helpers/articleUtils';
import { usePagination } from '@/hooks/usePagination';
import { Pagination } from '../non-sitecore/Pagination';
import { Author, Category, Tag } from '@/types/article';

export interface Article {
  Title: Field<string>;
  ShortDescription: Field<string>;
  Content: RichTextField;
  Image: ImageField;
  PublishedDate: Field<string>;
  Author: Author;
  Tags: Tag[];
  Category: Category;
}

interface ArticleListingProps extends ComponentProps {
  params: { [key: string]: string };
  fields: {
    items: {
      id: string;
      url: string;
      fields: Article;
    }[];
  };
}

export const Default = (props: ArticleListingProps) => {
  const { t } = useI18n();
  const { page } = useSitecore();
  const id = props.params.RenderingIdentifier;
  const searchBarPlaceholderKey = `article-listing-search-bar-${props.params.DynamicPlaceholderId}`;
  const recentPostsPlaceholderKey = `article-listing-side-bar-${props.params.DynamicPlaceholderId}`;
  const isPageEditing = page.mode.isEditing;

  // sort by latest published
  const articles = props.fields?.items
    .filter((article) => article.fields && Object.keys(article.fields)?.length > 0)
    .sort(sortByDateDesc);

  // categories with article counts
  const categoryCounts = getCategoryCounts(articles);

  // filter and pagination
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);

  const filteredArticles = selectedCategory
    ? articles.filter(
        (article) => article.fields?.Category?.fields?.Category?.value === selectedCategory
      )
    : articles;

  const articlesPerPage = 3;
  const { getPageSlice } = usePagination({
    totalItems: filteredArticles.length,
    currentPage,
    itemsPerPage: articlesPerPage,
    windowSize: 3,
  });

  const [startIndex, endIndex] = getPageSlice();
  const paginatedArticles = filteredArticles.slice(startIndex, endIndex);

  return (
    <section
      className={`component article-listing py-6 ${props?.params?.styles?.trimEnd()}`}
      id={id}
    >
      <div className="container grid grid-cols-1 gap-12 lg:grid-cols-[3fr_1fr]">
        {/* Left column */}
        <div className="space-y-16">
          {paginatedArticles.map((article) => (
            <article key={article.id} className="space-y-4">
              {/* Image */}
              <div className="relative aspect-3/2 w-full overflow-hidden rounded-lg md:aspect-9/4">
                <ContentSdkImage
                  field={article.fields?.Image}
                  className="h-full w-full object-cover"
                />
              </div>

              {/* Content */}
              <div className="space-y-3">
                <ContentSdkText
                  field={article.fields?.Title}
                  tag="h3"
                  className="font-semibold transition-colors"
                />

                {/* Icons */}
                <div className="text-foreground-light flex items-center gap-10 text-xs sm:text-sm">
                  {/* Author */}
                  {(article.fields?.Author?.fields?.AuthorName?.value || isPageEditing) && (
                    <span className="flex items-center gap-2">
                      <FontAwesomeIcon icon={faUser as IconProp} />
                      <ContentSdkText field={article.fields?.Author?.fields?.AuthorName} />
                    </span>
                  )}

                  {/* Published Date */}
                  {(() => {
                    const publishedDate = article.fields?.PublishedDate?.value;
                    const hasValidDate = publishedDate && !publishedDate.startsWith('0001-01-01');

                    if (!hasValidDate && !isPageEditing) {
                      return null;
                    }

                    return (
                      <span className="flex items-center gap-2">
                        {hasValidDate && (
                          <>
                            <FontAwesomeIcon icon={faCalendar as IconProp} />
                            <DateField
                              field={article.fields?.PublishedDate}
                              render={(date) =>
                                date
                                  ? new Date(date).toLocaleDateString('en-GB', {
                                      day: '2-digit',
                                      month: 'short',
                                      year: 'numeric',
                                    })
                                  : null
                              }
                            />
                          </>
                        )}
                      </span>
                    );
                  })()}

                  {/* Category */}
                  {(article.fields?.Category?.fields?.Category?.value || isPageEditing) && (
                    <span className="flex items-center gap-2">
                      <FontAwesomeIcon icon={faTag as IconProp} />
                      <ContentSdkText field={article.fields?.Category?.fields?.Category} />
                    </span>
                  )}
                </div>

                {/* Short Description */}
                <ContentSdkText
                  field={article.fields?.ShortDescription}
                  tag="p"
                  className="line-clamp-5 text-justify text-lg"
                />

                {/* Read More Button */}
                <Link href={article.url} className="arrow-btn" aria-label="Read full article">
                  {t('read_more_btn_text') || 'Read More'}
                </Link>
              </div>
            </article>
          ))}

          {/* Pagination */}
          <Pagination
            totalItems={filteredArticles.length}
            itemsPerPage={articlesPerPage}
            currentPage={currentPage}
            setCurrentPage={setCurrentPage}
          />
        </div>

        {/* Right column */}
        <div className="space-y-8 p-4">
          {/* Search placeholder */}
          <Placeholder name={searchBarPlaceholderKey} rendering={props.rendering} />

          {/* Categories */}
          <div className="text-foreground mb-5 text-lg font-bold">
            {t('categories_text') || 'Categories'}
          </div>
          <ul className="text-foreground-muted space-y-4 text-sm">
            <li className="flex justify-between">
              <button
                onClick={() => {
                  setSelectedCategory(null);
                  setCurrentPage(1);
                }}
                className={`flex-1 text-left ${!selectedCategory ? 'text-accent font-bold' : ''}`}
              >
                {t('show_all_text') || 'Show All'}
              </button>
              <span>{articles.length}</span>
            </li>
            {Object.values(categoryCounts).map((category) => (
              <li key={category.name} className="flex justify-between">
                <button
                  onClick={() => {
                    setSelectedCategory(category.name);
                    setCurrentPage(1);
                  }}
                  className={`flex-1 text-left ${
                    selectedCategory === category.name ? 'text-accent font-bold' : ''
                  }`}
                >
                  {category.name}
                </button>
                <span>{category.count}</span>
              </li>
            ))}
          </ul>

          {/* Side Bar Placeholder */}
          <Placeholder name={recentPostsPlaceholderKey} rendering={props.rendering} />
        </div>
      </div>
    </section>
  );
};

import type React from 'react';
import { Link, LinkFieldValue, useSitecore } from '@sitecore-content-sdk/nextjs';
import { ComponentProps } from '@/lib/component-props';
import { ChevronRight, MoreHorizontal } from 'lucide-react';
import { Home } from 'lucide-react';

type BreadcrumbPage = {
  id: string;
  name: string;
  title: { jsonValue: { value: string } };
  navigationTitle: { jsonValue: { value: string } };
  url: LinkFieldValue;
  navigationFilter: {
    jsonValue: {
      name: string;
    }[];
  };
};

type BreadcrumbProps = ComponentProps & {
  fields: {
    data: {
      datasource: BreadcrumbPage & {
        ancestors: BreadcrumbPage[];
      };
    };
  };
};

const hasNavFilter = (page: BreadcrumbPage, filterName: string): boolean => {
  return page?.navigationFilter?.jsonValue?.some((filter) => filter?.name === filterName) ?? false;
};

const getNavItemTitle = (item: BreadcrumbPage, truncate: boolean = true): string => {
  const MAX_TITLE_LENGTH = 20;
  const title = item.navigationTitle?.jsonValue.value || item.title?.jsonValue.value || item.name;
  return truncate && title.length > MAX_TITLE_LENGTH
    ? title.slice(0, MAX_TITLE_LENGTH) + ''
    : title;
};

export const Default = (props: BreadcrumbProps) => {
  const { fields, params } = props;
  const { styles, RenderingIdentifier: id, NavigationFilter: filterName } = params;
  const item = fields?.data?.datasource ?? {};

  const { page } = useSitecore();

  const visibleAncestors = item.ancestors.filter((ancestor) => !hasNavFilter(ancestor, filterName));
  const showItem = !hasNavFilter(item, filterName);

  const isTemplateEditing =
    page.mode?.isEditing &&
    (page.layout.sitecore.route?.templateName === 'Partial Design' ||
      page.layout.sitecore.route?.templateName === 'Page Design');

  if (!showItem || !visibleAncestors.length) {
    if (isTemplateEditing) {
      return (
        <div className={`component breadcrumb ${styles}`} id={id}>
          [BREADCRUMB NAVIGATION]
        </div>
      );
    }
    return null;
  }

  const reversedAncestors = [...visibleAncestors].reverse();
  const homeAncestor = reversedAncestors[0];
  const intermediateAncestors = reversedAncestors.slice(1, -1);
  const lastAncestor = reversedAncestors[reversedAncestors.length - 1];

  const hasIntermediateAncestors = !!intermediateAncestors.length;
  const hasLastAncestor = !!lastAncestor && lastAncestor?.id !== homeAncestor?.id;

  return (
    <nav
      aria-label="breadcrumb"
      className={`component breadcrumb bg-background-muted ${styles}`}
      id={id}
    >
      <ol className="container flex items-center gap-4 overflow-auto py-4 lg:py-7">
        {homeAncestor && (
          <li key={homeAncestor.id} className="text-foreground-light flex items-center gap-4">
            <Link
              field={homeAncestor.url}
              className="hover:text-foreground whitespace-nowrap transition-colors"
              title={getNavItemTitle(homeAncestor, false)}
            >
              <span className="hidden max-md:inline">
