import { useEffect, useState } from 'react';
import { useI18n } from 'next-localization';
import { getCart, CartItem } from '@/lib/cart';
import {
  NextImage as ContentSdkImage,
  Text as ContentSdkText,
  Link,
  LinkField,
} from '@sitecore-content-sdk/nextjs';
import QuantityControl from './QuantityControl';
import { useLocale } from '@/hooks/useLocaleOptions';
import { Heart, X } from 'lucide-react';
import { useCartAction } from '@/hooks/useCartAction';
import { PopoverClose } from '@radix-ui/react-popover';

export const MiniCart = ({
  showWishlist,
  checkoutPage,
}: {
  showWishlist?: boolean;
  checkoutPage: LinkField;
}) => {
  const [cart, setCart] = useState<CartItem[]>([]);
  const { t } = useI18n();
  const { currencySymbol } = useLocale();
  const { updatingItemId, handleRemoveFromCart, handleUpdateQuantity } = useCartAction();

  useEffect(() => {
    setCart(getCart());
    const handler = () => setCart(getCart());
    window.addEventListener('storage', handler);
    return () => window.removeEventListener('storage', handler);
  }, []);

  const handleRemove = async (cartItemId: string) => {
    const updatedCart = await handleRemoveFromCart(cartItemId);
    setCart(updatedCart);
  };

  const handleQuantity = async (cartItemId: string, qty: number) => {
    const updatedCart = await handleUpdateQuantity(cartItemId, qty);
    setCart(updatedCart);
  };

  if (!cart.length) {
    return (
      <div>
        <h5>{t('shopping_cart_label') || 'Shopping Cart'}</h5>
        <hr className="mt-4 mb-6" />
        <p>{t('cart-empty') || 'Your cart is empty.'}</p>
      </div>
    );
  }

  const subtotal = cart.reduce((sum, item) => {
    const price = Number(item.product.Price?.value) || 0;
    return sum + price * item.quantity;
  }, 0);
  const vat = subtotal * 0.2;
  const total = subtotal + vat;

  return (
    <div className="flex max-h-[75vh] flex-col">
      <div className="px-2">
        <h5>{t('shopping_cart_label') || 'Shopping Cart'}</h5>
        <hr className="mt-4" />
      </div>
      <ul className="custom-scrollbar shrink space-y-6 overflow-auto px-2 py-6">
        {cart.map((item) => {
          const isUpdating = updatingItemId === item.id;

          return (
            <li
              key={item.id}
              className={`grid grid-cols-[auto_auto_1fr] gap-4 sm:grid-cols-[auto_auto_1fr_auto] ${isUpdating ? 'pointer-events-none opacity-50' : ''}`}
            >
              <div className="space-y-1 self-center">
                {showWishlist && (
                  <button className="surface-btn">
                    <Heart className="size-4" strokeWidth={3} />
                  </button>
                )}
                <button onClick={() => handleRemove(item.id)} className="surface-btn">
                  <X className="size-4" strokeWidth={3} />
                </button>
              </div>
              <div className="bg-background-surface size-19 rounded-sm p-2">
                <ContentSdkImage field={item.product.Image1} className="image-contain" />
              </div>
              <div>
                <h6 className="line-clamp-1 text-lg break-all">
                  <ContentSdkText field={item.product.Title} />
                </h6>
                <p>
                  <span>{item.size?.fields.ProductSize.value}</span>
                  {item.size?.fields.ProductSize.value && item.color?.fields.Name.value && ' | '}
                  <span>{item.color?.fields.Name.value}</span>
                </p>
                <div className="flex flex-wrap justify-between">
                  <QuantityControl
                    quantity={item.quantity}
                    onChange={(q) => handleQuantity(item.id, q)}
                  />
                  <span className="text-lg font-bold sm:hidden">
                    {currencySymbol} {(Number(item.product.Price.value) * item.quantity).toFixed(2)}
                  </span>
                </div>
              </div>
              <span className="text-lg font-bold max-sm:hidden">
                {currencySymbol} {(Number(item.product.Price.value) * item.quantity).toFixed(2)}
              </span>
            </li>
          );
        })}
      </ul>
      <div className="px-2">
        <hr className="mb-4" />

        <p className="flex justify-between">
          <span>{t('subtotal_label') || 'Subtotal:'}</span>
          <span>
            {currencySymbol}
            {subtotal.toFixed(2)}
          </span>
        </p>
        <p className="flex justify-between">
          <span>{t('vat_label') || 'VAT (20%):'}</span>
          <span>
            {currencySymbol} {vat.toFixed(2)}
          </span>
        </p>
        <p className="mb-6 flex justify-between text-lg font-bold">
          <span>{t('total_label') || 'Total:'}</span>
          <span>
            {currencySymbol} {total.toFixed(2)}
          </span>
        </p>
        <PopoverClose asChild>
          <Link field={checkoutPage} className="main-btn" />
        </PopoverClose>
      </div>
    </div>
  );
};

import { usePagination } from '@/hooks/usePagination';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { useI18n } from 'next-localization';

interface PaginationProps {
  totalItems: number;
  itemsPerPage: number;
  currentPage: number;
  setCurrentPage: (page: number) => void;
  windowSize?: number;
  paginationButtonClasses?: string;
}

export const Pagination = ({
  totalItems,
  itemsPerPage,
  currentPage,
  setCurrentPage,
  windowSize = 3,
  paginationButtonClasses = 'px-3 py-2 md:px-4 md:py-3 rounded-md',
}: PaginationProps) => {
  const { totalPages, startPage, endPage, visiblePages } = usePagination({
    totalItems,
    currentPage,
    itemsPerPage,
    windowSize,
  });

  const { t } = useI18n();
  if (totalPages <= 1) return null;
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="mt-12 flex items-center justify-center space-x-2 text-sm md:space-x-4">
      {currentPage > 1 && (
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          className={`${paginationButtonClasses} bg-background-accent`}
        >
          <span className="md:hidden">
            <ChevronLeft size={16} />
          </span>
          <span className="hidden md:block">{t('pagination_prev_btn_text') || 'Prev'}</span>
        </button>
      )}

      {startPage > 1 && (
        <>
          <button
            onClick={() => handlePageChange(1)}
            className={`${paginationButtonClasses} ${
              currentPage === 1 ? 'bg-accent text-background' : 'bg-background-accent'
            }`}
          >
            1
          </button>
          <span className="md:px-2">...</span>
        </>
      )}

      {visiblePages.map((page) => (
        <button
          key={page}
          onClick={() => handlePageChange(page)}
          className={`${paginationButtonClasses} ${
            page === currentPage ? 'bg-accent text-background' : 'bg-background-accent'
          }`}
        >
          {page}
        </button>
      ))}

      {endPage < totalPages && (
        <>
          <span className="md:px-2">...</span>
          <button
            onClick={() => handlePageChange(totalPages)}
            className={`${paginationButtonClasses} ${
              currentPage === totalPages ? 'bg-accent text-background' : 'bg-background-accent'
            }`}
          >
            {totalPages}
          </button>
        </>
      )}

      {currentPage < totalPages && (
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          className={`${paginationButtonClasses} bg-background-accent`}
        >
          <span className="md:hidden">
            <ChevronRight size={16} />
          </span>
          <span className="hidden md:block">{t('pagination_next_btn_text') || 'Next'}</span>
        </button>
      )}
    </div>
  );
};

import { NextImage as ContentSdkImage, Text } from '@sitecore-content-sdk/nextjs';
import StarRating from './StarRating';
import Link from 'next/link';
import { Product } from '@/types/products';
import { useLocale } from '@/hooks/useLocaleOptions';

interface ProductCardProps {
  product: Partial<Product> & {
    Rating: number;
  };
  url: string;
  className?: string;
}

export const ProductCard = ({ product, url, className }: ProductCardProps) => {
  const { currencySymbol } = useLocale();
  const formattedPrice =
    product.Price?.value && !isNaN(product.Price?.value)
      ? product.Price.value.toLocaleString(undefined, {
          minimumFractionDigits: 0,
          maximumFractionDigits: 2,
        })
      : product.Price?.value;

  return (
    <Link href={url} passHref>
      <div
        className={`flex min-h-123 w-full flex-col overflow-hidden rounded-2xl hover:drop-shadow-sm ${className}`}
      >
        {/* Product Image */}
        <div className="bg-background-surface flex h-72 w-full items-center justify-center p-6">
          <ContentSdkImage
            field={product.Image1}
            className="max-h-full max-w-full object-contain"
            priority
          />
        </div>

        {/* Product Details */}
        <div className="bg-background flex grow-1 flex-col items-start px-5 pt-3 pb-9 text-left">
          <p className="!text-foreground-light">
            <Text field={product.Category?.fields?.CategoryName} />
          </p>

          <h6 className="!text-foreground mt-1 line-clamp-2 font-semibold">
            <Text field={product.Title} />
          </h6>

          <StarRating
            rating={product.Rating || 0}
            showOnlyFilled
            className="!text-accent mt-1 mb-5"
          />

          <h6 className="!text-foreground mt-auto font-semibold">
            <span className="mr-1 align-super text-sm">{currencySymbol} </span>
            {formattedPrice}
          </h6>
        </div>
      </div>
    </Link>
  );
};

import { useId } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { A11y, Autoplay, Keyboard, Navigation } from 'swiper/modules';
import { ProductCard } from '../non-sitecore/ProductCard';
import { Product } from '@/types/products';
import { SitecoreItem } from '@/types/common';
import CarouselButton from './CarouselButton';
import { calculateAverageRating } from '@/helpers/productUtils';

interface ProductCarouselProps {
  products: SitecoreItem<Product>[];
  loop?: boolean;
  autoPlay?: boolean;
  autoPlayDelay?: number;
  className?: string;
}

const ProductCarousel = ({
  products,
  loop = true,
  autoPlay = true,
  autoPlayDelay = 5000,
  className = '',
}: ProductCarouselProps) => {
  const uid = useId();

  return (
    <div className={`relative w-full ${className}`}>
      <CarouselButton
        direction="prev"
        className={`product-carousel-prev-${uid} absolute top-1/2 -left-5 -translate-y-1/2`}
        name="previous-product"
        aria-label="Previous product"
      />

      <Swiper
        modules={[Navigation, Autoplay, Keyboard, A11y]}
        navigation={{
          prevEl: `.product-carousel-prev-${uid}`,
          nextEl: `.product-carousel-next-${uid}`,
          disabledClass: 'pointer-events-none opacity-50',
        }}
        slidesPerView={1}
        breakpoints={{
          768: { slidesPerView: 2, spaceBetween: 40 },
          1024: { slidesPerView: 3, spaceBetween: 30 },
          1280: { slidesPerView: 4, spaceBetween: 20 },
          1536: { slidesPerView: 5, spaceBetween: 20 },
        }}
        a11y={{ enabled: true }}
        keyboard={{ enabled: true }}
        loop={loop}
        autoplay={
          autoPlay
            ? {
                delay: autoPlayDelay,
                pauseOnMouseEnter: true,
              }
            : false
        }
      >
        {products
          .filter((product) => Object.keys(product.fields).length > 0) // Prevent mapping over the items with no fields/Data folder
          .map((product) => (
            <SwiperSlide key={product.id} className="p-1">
              <ProductCard
                product={{
                  ...product.fields,
                  Rating: calculateAverageRating(product.fields.Reviews || []),
                }}
                url={product.url}
              />
            </SwiperSlide>
          ))}
      </Swiper>

      <CarouselButton
        direction="next"
        className={`product-carousel-next-${uid} absolute top-1/2 -right-5 -translate-y-1/2`}
        name="next-product"
        aria-label="Next product"
      />
    </div>
  );
};

export default ProductCarousel;

import { Product } from '@/types/products';

interface ProductColorControlProps {
  colors?: Product['Color'];
  selectedColor?: Product['Color'][number];
  onSelect: (color: Product['Color'][number]) => void;
}

export const ProductColorControl = ({
  colors = [],
  selectedColor,
  onSelect,
