};

import { Text as ContentSdkText, useSitecore } from '@sitecore-content-sdk/nextjs';
import { Product } from '@/types/products';
import StarRating from '../non-sitecore/StarRating';
import { useLocale } from '@/hooks/useLocaleOptions';
import { calculateAverageRating } from '@/helpers/productUtils';

interface ProductDescriptionProps {
  product: Product;
}

export const ProductDescription = ({ product }: ProductDescriptionProps) => {
  const { page } = useSitecore();
  const isPageEditing = page.mode.isEditing;
  const { currency } = useLocale();

  const reviews = product?.Reviews || [];
  const reviewCount = reviews.length;
  const averageRating = calculateAverageRating(reviews);

  return (
    <>
      <h1 className="pt-3 text-4xl font-bold lg:pt-0">
        <ContentSdkText field={product.Title} />
      </h1>

      {(product?.Price?.value || isPageEditing) && (
        <p className="text-xl">
          {currency} <ContentSdkText field={product.Price} />
        </p>
      )}

      {!!product?.Reviews?.length && (
        <div className="flex items-center space-x-3">
          <span className="text-foreground text-lg">{averageRating}</span>
          <StarRating rating={averageRating} className="!text-accent" />
          <div className="bg-foreground-muted h-7 w-px" />
          <span className="text-foreground-muted text-sm">
            {reviewCount} Customer Review{reviewCount !== 1 ? 's' : ''}
          </span>
        </div>
      )}

      {(product?.ShortDescription?.value || isPageEditing) && (
        <p className="text-foreground text-lg">
          <ContentSdkText field={product.ShortDescription} />
        </p>
      )}
    </>
  );
};

import { ImageField, NextImage as ContentSdkImage } from '@sitecore-content-sdk/nextjs';
import { useMemo, useState } from 'react';
import { Product } from '@/types/products';

interface ProductGalleryProps {
  product: Product;
}

export const ProductGallery = ({ product }: ProductGalleryProps) => {
  const [mainImageIndex, setMainImageIndex] = useState(0);

  const images: ImageField[] = useMemo(
    () =>
      [product.Image1, product.Image2, product.Image3, product.Image4, product.Image5].filter(
        (img): img is ImageField => Boolean(img?.value?.src)
      ),
    [product]
  );

  return (
    <div className="flex w-full flex-col-reverse gap-3 sm:flex-row">
      <div className="flex gap-3 sm:flex-col sm:justify-start">
        {images.map((img, idx) => {
          const isActive = idx === mainImageIndex;

          return (
            <button
              key={img?.value?.src || idx}
              type="button"
              onClick={() => setMainImageIndex(idx)}
              disabled={isActive}
              aria-label={`View image ${idx + 1}`}
              className={`bg-background-muted focus:ring-accent size-15 overflow-hidden rounded focus:ring-2 focus:outline-none xl:size-18 ${
                isActive ? 'cursor-not-allowed opacity-50' : 'hover:ring-accent hover:ring-2'
              }`}
            >
              <ContentSdkImage field={img} className="h-full w-full object-cover" />
            </button>
          );
        })}
      </div>

      <div className="grow">
        {images[mainImageIndex] ? (
          <ContentSdkImage
            field={images[mainImageIndex]}
            className="bg-background-muted aspect-square w-full rounded-md object-contain p-4"
          />
        ) : (
          <div className="bg-background-muted aspect-square w-full rounded-md" />
        )}
      </div>
    </div>
  );
};

import { Text as ContentSdkText, useSitecore } from '@sitecore-content-sdk/nextjs';
import { useEffect, useState } from 'react';
import SocialShare from '../non-sitecore/SocialShare';
import { useI18n } from 'next-localization';
import { Product } from '@/types/products';

interface ProductMetaDetalsProps {
  product: Product;
}

export const ProductMetaDetals = ({ product }: ProductMetaDetalsProps) => {
  const { page } = useSitecore();
  const { t } = useI18n();

  const isPageEditing = page.mode.isEditing;

  const [currentUrl, setCurrentUrl] = useState('');

  useEffect(() => {
    if (typeof window !== 'undefined') {
      setCurrentUrl(window.location.href);
    }
  }, [product]);

  return (
    <>
      <div className="text-foreground-light mx-0 border-t pt-10 text-sm sm:pb-6 lg:col-start-2 lg:mx-10">
        <dl className="grid grid-cols-[auto_16px_1fr] gap-x-2 gap-y-4">
          {(product?.SKU?.value || isPageEditing) && (
            <>
              <dt>{t('product_sku_label') || 'SKU'}</dt>
              <dd className="text-center">:</dd>
              <dd>
                <ContentSdkText field={product.SKU} />
              </dd>
            </>
          )}

          {product.Category?.fields?.CategoryName?.value && (
            <>
              <dt>{t('product_category_label') || 'Category'}</dt>
              <dd className="text-center">:</dd>
              <dd>{product.Category?.fields?.CategoryName?.value}</dd>
            </>
          )}

          {Array.isArray(product?.Tags) && product.Tags.length > 0 && (
            <>
              <dt>{t('product_tags_label') || 'Tags'}</dt>
              <dd className="text-center">:</dd>
              <dd>{product.Tags.map((t) => t.fields.Tag.value).join(', ')}</dd>
            </>
          )}

          <dt className="flex items-center">{t('product_share_label') || 'Share'}</dt>
          <dd className="flex items-center justify-center">:</dd>
          <dd className="mr-1">
            <SocialShare
              url={currentUrl}
              title={product.Title?.value ?? ''}
              round={true}
              className="flex flex-wrap gap-3"
              iconClassName="size-8"
            />
          </dd>
        </dl>
      </div>
    </>
  );
};

import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import CarouselButton from './CarouselButton';
import { ReviewFields } from '@/types/review';
import { ComponentRendering, useSitecore } from '@sitecore-content-sdk/nextjs';
import ReviewCard from './ReviewCard';
import { useI18n } from 'next-localization';

type ProductReviewsProps = {
  reviews: ReviewFields[];
  rendering: ComponentRendering;
};

export const ProductReviews = (props: ProductReviewsProps) => {
  const { page } = useSitecore();
  const { t } = useI18n();

  const uid = props.rendering.uid;
  const isPageEditing = page.mode.isEditing;

  if (!props.reviews || props.reviews.length === 0) {
    return (
      <div className="w-full">
        <div className="container pt-10">
          <p className="pb-10 text-center">
            {t('no_reviews_label') || 'No reviews available for this product.'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full">
      <div className="relative pt-10">
        {/* Previous Button */}
        <CarouselButton
          direction="prev"
          name="Previous Review"
          aria-label="Previous Review"
          className={`swiper-btn-prev-${uid} absolute top-1/3 -left-5`}
        />

        {/* Swiper Slider */}
        <Swiper
          modules={[Navigation, Pagination]}
          spaceBetween={20}
          slidesPerView={4}
          navigation={{
            prevEl: `.swiper-btn-prev-${uid}`,
            nextEl: `.swiper-btn-next-${uid}`,
            disabledClass: 'pointer-events-none opacity-50',
          }}
          breakpoints={{
            320: { slidesPerView: 1 },
            640: { slidesPerView: 2 },
            1024: { slidesPerView: 3 },
            1280: { slidesPerView: 4 },
          }}
          className="!overflow-visible !overflow-x-clip"
        >
          {props.reviews.map((review, index) => (
            <SwiperSlide key={index} className="pb-10">
              <ReviewCard isPageEditing={isPageEditing} {...review} />
            </SwiperSlide>
          ))}
        </Swiper>

        {/* Next Button */}
        <CarouselButton
          direction="next"
          name="Next Review"
          aria-label="Next Review"
          className={`swiper-btn-next-${uid} absolute top-1/3 -right-5`}
        />
      </div>
    </div>
  );
};

import { Product } from '@/types/products';

interface ProductSizeControlProps {
  sizes?: Product['Size'];
  selectedSize?: Product['Size'][number];
  onSelect: (size: Product['Size'][number]) => void;
}

export const ProductSizeControl = ({
  sizes = [],
  selectedSize,
  onSelect,
}: ProductSizeControlProps) => {
  if (!sizes.length) return null;

  return (
    <div className="flex gap-3">
      {sizes.map((size) => (
        <button
