import { Placeholder, useSitecore } from '@sitecore-content-sdk/nextjs';
import { useEffect, useState } from 'react';
import { ComponentProps } from '@/lib/component-props';
import { Heart, Plus } from 'lucide-react';
import { isParamEnabled } from '@/helpers/isParamEnabled';
import { useI18n } from 'next-localization';
import { Product } from '@/types/products';
import { ProductTabs } from '../non-sitecore/ProductTabs';
import QuantityControl from '../non-sitecore/QuantityControl';
import { AddToCartButton } from '../non-sitecore/AddToCartButton';
import { ProductGallery } from '../non-sitecore/ProductGallery';
import { ProductMetaDetals } from '../non-sitecore/ProductMetaDetails';
import { ProductDescription } from '../non-sitecore/ProductDescription';
import { ProductSizeControl } from '../non-sitecore/ProductSizeControl';
import { ProductColorControl } from '../non-sitecore/ProductColorControl';

interface ProductDetailsProps extends ComponentProps {
  params: { [key: string]: string };
  fields: Product;
}

export const Default = (props: ProductDetailsProps) => {
  const { page } = useSitecore();
  const { t } = useI18n();

  const id = props?.params?.RenderingIdentifier;
  const styles = `${props?.params?.styles || ''}`.trim();
  const isPageEditing = page.mode.isEditing;

  const product = props?.fields;
  const productId = page.layout.sitecore.route?.itemId;

  const ShowCompareButton = isParamEnabled(props?.params?.ShowCompareButton);
  const ShowAddtoCartButton = isParamEnabled(props?.params?.ShowAddtoCartButton);
  const ShowAddtoWishlistButton = isParamEnabled(props?.params?.ShowAddtoWishlistButton);

  const relatedProductsPlaceholderKey = `related-products-${props?.params?.DynamicPlaceholderId}`;

  const [selectedColor, setSelectedColor] = useState(product?.Color?.[0]);
  const [selectedSize, setSelectedSize] = useState(product?.Size?.[0]);
  const [selectedQuantity, setSelectedQuantity] = useState(1);

  useEffect(() => {
    setSelectedColor(product?.Color?.[0]);
    setSelectedSize(product?.Size?.[0]);
    setSelectedQuantity(1);
  }, [product?.Color, product?.Size, productId]);

  if (!props.fields?.Title) {
    return isPageEditing ? (
      <div className={`component article-listing py-6 ${styles}`} id={id}>
        [Product Details]
      </div>
    ) : (
      <></>
    );
  }

  return (
    <section className={`component article-listing py-6 ${styles}`} id={id}>
      <div className="container">
        <div className="grid grid-cols-1 lg:grid-cols-2">
          {/* Left image section */}
          <ProductGallery product={product} key={productId} />

          {/* Right product info */}
          <div className="max-w-xl space-y-4 pb-4 lg:px-10">
            <ProductDescription product={product} />

            <div className="flex flex-wrap justify-between gap-4 py-5">
              {/* Sizes */}
              {!!product?.Size?.length && (
                <div>
                  <p className="mb-2 text-sm">{t('product_size_label') || 'Size'}</p>
                  <ProductSizeControl
                    sizes={product.Size}
                    selectedSize={selectedSize}
                    onSelect={setSelectedSize}
                  />
                </div>
              )}

              {/* Colors */}
              {!!product?.Color?.length && (
                <div>
                  <p className="mb-2 text-sm">{t('product_color_label') || 'Color'}</p>
                  <ProductColorControl
                    colors={product.Color}
                    selectedColor={selectedColor}
                    onSelect={setSelectedColor}
                  />
                </div>
              )}

              {/* Quantity */}
              <div>
                <p className="mb-2 text-sm">{t('product_quantity_label') || 'Quantity'}</p>
                <QuantityControl
                  quantity={selectedQuantity}
                  onChange={setSelectedQuantity}
                  isLarge
                />
              </div>
            </div>

            {/* Add to cart */}
            {ShowAddtoCartButton && (
              <AddToCartButton
                productId={productId || ''}
                product={product}
                selectedQuantity={selectedQuantity}
                selectedColor={selectedColor}
                selectedSize={selectedSize}
              />
            )}

            {/* Action Buttons */}
            <div className="flex flex-wrap gap-x-10 gap-y-4">
              {ShowCompareButton && (
                <button className="action-btn">
                  <Plus className="size-5" strokeWidth={3} />
                  {t('compare_btn_text') || 'Compare'}
                </button>
              )}

              {ShowAddtoWishlistButton && (
                <button className="action-btn">
                  <Heart className="size-5" strokeWidth={3} />
                  {t('wishlist_btn_text') || 'Add to Wishlist'}
                </button>
              )}
            </div>
          </div>

          <ProductMetaDetals product={product} />
        </div>
      </div>

      <ProductTabs
        product={product}
        isPageEditing={isPageEditing}
        dynamicPlaceholderId={props.params.DynamicPlaceholderId}
        rendering={props.rendering}
      />

      <Placeholder name={relatedProductsPlaceholderKey} rendering={props.rendering} />
    </section>
  );
};

import { usePagination } from '@/hooks/usePagination';
import { ComponentProps } from '@/lib/component-props';
import { useI18n } from 'next-localization';
import { useState } from 'react';
import { ProductCard } from '@/components/non-sitecore/ProductCard';
import { Pagination } from '../non-sitecore/Pagination';
import { ChevronDown } from 'lucide-react';
import { ProductIGQL } from '@/types/products';
import { calculateAverageRatingFromIGQL } from '@/helpers/productUtils';

interface ProductCategory {
  id: string;
  name: string;
  children: {
    results: ProductIGQL[];
  };
}

interface ProductListingProps extends ComponentProps {
  params: { [key: string]: string };
  fields: {
    data: {
      contextItem: {
        children: {
          results: ProductCategory[] | ProductIGQL[];
        };
      };
    };
  };
}

export const Default = (props: ProductListingProps) => {
  const { t } = useI18n();
  const id = props.params.RenderingIdentifier;
  const items = props.fields.data.contextItem.children.results;

  const unformattedProducts = items
    .filter((item) => Object.keys(item).length !== 0)
    .flatMap((item) => {
      // Check if the item has children property (ProductCategory)
      if ('children' in item && item.children?.results) {
        return item.children.results;
      }
      // Otherwise, it's a Product itself
      return [item as ProductIGQL];
    });

  const products = unformattedProducts.map((product) => {
    return {
      Title: product.title.jsonValue,
      Price: product.price.jsonValue,
      Image1: product.image1.jsonValue,
      Category: product.category.jsonValue,
      Rating: calculateAverageRatingFromIGQL(product.reviews?.targetItems || []),
      id: product.id,
      url: product.url.path,
    };
  });

  const sortOptions = [
    { value: 'default', label: t('product-listing-sort-default') || 'Default' },
    { value: 'price-asc', label: t('product-listing-sort-price-asc') || 'Price: Low to High' },
    { value: 'price-desc', label: t('product-listing-sort-price-desc') || 'Price: High to Low' },
    { value: 'name-asc', label: t('product-listing-sort-name-asc') || 'Name: A to Z' },
    { value: 'name-desc', label: t('product-listing-sort-name-desc') || 'Name: Z to A' },
  ];

  const [displayCount, setDisplayCount] = useState(12);
  const [sortOption, setSortOptions] = useState(sortOptions[0].value);
  const [currentPage, setCurrentPage] = useState(1);

  const { getPageSlice } = usePagination({
    totalItems: products.length,
    currentPage,
    itemsPerPage: displayCount,
    windowSize: 3,
  });

  const [startIndex, endIndex] = getPageSlice();

  const sortedProducts = [...products].sort((a, b) => {
    switch (sortOption) {
      case 'price-asc':
        return (a.Price?.value || 0) - (b.Price?.value || 0);
      case 'price-desc':
        return (b.Price?.value || 0) - (a.Price?.value || 0);
      case 'name-asc':
        return (a.Title?.value || '').localeCompare(b.Title?.value || '');
      case 'name-desc':
        return (b.Title?.value || '').localeCompare(a.Title?.value || '');
      default:
        return 0;
    }
  });

  const currentPageProducts = sortedProducts.slice(startIndex, endIndex);

  const handleDisplayCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = Number(e.target.value);
    if (value >= 1 && value <= 100) {
      setDisplayCount(value);
      setCurrentPage(1);
    }
  };

  return (
    <section className={`component product-listing ${props?.params.styles.trimEnd()}`} id={id}>
      {/* Sort and Filter */}
      <div className="bg-background-accent">
        <div className="container flex flex-col justify-between gap-5 py-5 sm:flex-row sm:items-center">
          <div>
            {t('product-listing-showing') || 'Showing'} {Math.min(products.length, startIndex + 1)}{' '}
            - {Math.min(endIndex, products.length)} {t('product-listing-of') || 'of'}{' '}
            {products.length} {t('product-listing-results') || 'results'}
          </div>

          {/* Sort Options */}
          <div className="gap flex items-center gap-x-7">
            <div>
              <label htmlFor="items-per-page" className="me-4 text-lg">
                {t('product-listing-show') || 'Show'}
              </label>
              <input
                type="number"
                id="items-per-page"
                className="bg-background outline-border text-foreground-light size-14 rounded-md text-center text-lg [&::-webkit-inner-spin-button]:appearance-none"
                value={displayCount}
                onChange={handleDisplayCountChange}
              />
            </div>

            <div className="flex flex-col sm:flex-row sm:items-center">
              <label htmlFor="sort-by" className="me-4 text-lg">
                {t('product-listing-sort-by') || 'Sort by'}
              </label>
              <div className="relative">
                <select
                  id="sort-by"
                  className="bg-background text-foreground-light outline-border h-14 w-full appearance-none rounded-md px-7 pr-17 text-lg"
                  value={sortOption}
                  onChange={(e) => setSortOptions(e.target.value)}
                >
                  {sortOptions.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
                <ChevronDown className="text-foreground-light pointer-events-none absolute top-1/2 right-7 size-7 -translate-y-1/2 transform" />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Products */}
      <div className="container mt-14 mb-20">
        {/* Product Grid */}
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 xl:gap-10 2xl:grid-cols-6">
          {currentPageProducts.map(({ id, url, ...rest }) => (
            <ProductCard key={id} product={rest} url={url} />
          ))}
        </div>

        {/* Pagination */}
        <Pagination
          totalItems={products.length}
          itemsPerPage={displayCount}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
        />
      </div>
    </section>
  );
};

import React, { JSX } from 'react';
import {
  NextImage as ContentSdkImage,
  RichText as ContentSdkRichText,
  Field,
  ImageField,
  Link,
  LinkField,
  RichTextField,
  Text,
} from '@sitecore-content-sdk/nextjs';
import { ComponentProps } from 'lib/component-props';
import clsx from 'clsx';
import AccentLine from '@/assets/icons/accent-line/AccentLine';
import { Quote } from '@/assets/icons/quote/Quote';
import { CommonStyles, LayoutStyles, PromoFlags } from '@/types/styleFlags';

interface Fields {
  PromoImageOne: ImageField;
  PromoImageTwo: ImageField;
  PromoImageThree: ImageField;
  PromoTitle: Field<string>;
  PromoDescription: RichTextField;
  PromoSubTitle: Field<string>;
  PromoMoreInfo: LinkField;
}

type PromoImageGroupProps = Partial<
  Pick<Fields, 'PromoImageOne' | 'PromoImageTwo' | 'PromoImageThree'>
> & {
  withShapes?: boolean;
  withShadows?: boolean;
};

export type PromoProps = ComponentProps & {
  params: { [key: string]: string };
  fields: Fields;
};

const isShadowClassActive = (val: boolean) => (val ? 'shadow-2xl' : '');

export const PromoContent = ({ ...props }) => {
  const isAccentLineVisible = !props?.params?.styles?.includes(CommonStyles.HideAccentLine);

  return (
    <div className="space-y-5">
      <div className="eyebrow">
        <Text field={props.fields.PromoSubTitle} />
      </div>

      <h2 className="inline-block max-w-md">
        <Text field={props.fields.PromoTitle} />
        {isAccentLineVisible && <AccentLine className="w-full max-w-xs" />}
      </h2>

      <div className="max-w-lg text-lg">
        <ContentSdkRichText field={props.fields.PromoDescription} />
      </div>

      <Link field={props.fields.PromoMoreInfo} className="arrow-btn" />
    </div>
  );
};

export const SingleImageContainer = ({
  PromoImageOne,
  withShapes,
  withShadows,
}: PromoImageGroupProps): JSX.Element => {
  const shadowClass = isShadowClassActive(withShadows ?? false);
  return (
    <>
      {withShapes && (
        <div className="bg-background-muted absolute top-0 left-0 z-0 aspect-6/5 w-2/3 rounded-2xl"></div>
      )}
      <div>
        <div className={clsx({ 'm-4 md:m-9 md:mb-6 xl:m-15 xl:mb-8': withShapes })}>
          {withShapes && (
            <div className="bg-background-muted absolute top-1/2 right-0 z-0 aspect-5/3 w-3/4 -translate-y-1/2 transform rounded-2xl"></div>
          )}
          <div
            className={`relative z-10 aspect-4/3 w-full max-w-4xl overflow-hidden rounded-2xl ${shadowClass}`}
          >
            <ContentSdkImage field={PromoImageOne} className="h-full w-full object-cover" />
          </div>
        </div>
      </div>
    </>
  );
};

export const MultipleImageContainer = ({
  PromoImageOne,
  PromoImageTwo,
  PromoImageThree,
  withShapes,
  withShadows,
}: PromoImageGroupProps): JSX.Element => {
  const shadowClass = isShadowClassActive(withShadows ?? false);
  const marginClass = withShapes ? 'mr-4' : '';

  return (
    <>
      <div className="flex flex-col items-center gap-8 md:flex-row">
        <div className="flex flex-col gap-10 md:w-1/3">
          <div className="relative aspect-square overflow-visible rounded-2xl">
            <div
              className={`relative z-10 h-full w-full overflow-hidden rounded-2xl ${shadowClass}`}
            >
              <ContentSdkImage field={PromoImageTwo} className="h-full w-full object-cover" />
            </div>
          </div>
          <div className="relative aspect-2/3 overflow-visible rounded-2xl">
            <div
              className={`relative z-10 h-full w-full overflow-hidden rounded-2xl ${shadowClass}`}
            >
              <ContentSdkImage field={PromoImageThree} className="h-full w-full object-cover" />
            </div>
          </div>
        </div>
        <div className="relative w-full md:w-2/3">
          {withShapes && (
            <div className="bg-background-muted absolute right-0 z-0 aspect-[495/422] w-3/4 rounded-2xl md:-top-10 xl:-top-15"></div>
          )}
          <div className={`relative aspect-3/2 overflow-visible rounded-2xl ${marginClass} z-10`}>
            <div
              className={`relative z-10 h-full w-full overflow-hidden rounded-2xl ${shadowClass}`}
            >
              <ContentSdkImage
                field={PromoImageOne}
                className="absolute inset-0 h-full w-full object-cover"
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export const Default = (props: PromoProps): JSX.Element => {
  const id = props.params.RenderingIdentifier;
  const isPromoReversed = !props?.params?.styles?.includes(LayoutStyles.Reversed)
    ? ''
    : 'order-last';
  const showSingleImage = !props?.params?.styles?.includes(PromoFlags.ShowMultipleImages);
  const withShapes = !props?.params?.styles?.includes(PromoFlags.HidePromoShapes);
  const withShadows = !props?.params?.styles?.includes(PromoFlags.HidePromoShadows);

  const justifyContentClass = !showSingleImage ? 'justify-self-start' : '';
  const firstColumnSize = showSingleImage ? 'lg:col-span-6' : 'lg:col-span-7';
  const secondColumnSize = showSingleImage ? 'lg:col-span-6' : 'lg:col-span-5';

  return (
    <section className={`${props.params.styles} py-20`} id={id ? id : undefined}>
      <div className="container grid grid-cols-1 place-items-center gap-10 lg:grid-cols-12">
        <div className={`${isPromoReversed} col-span-full ${firstColumnSize} relative w-full`}>
          {showSingleImage ? (
            <SingleImageContainer
              PromoImageOne={props.fields.PromoImageOne}
              withShapes={withShapes}
              withShadows={withShadows}
            />
          ) : (
            <MultipleImageContainer
              PromoImageOne={props.fields.PromoImageOne}
              PromoImageTwo={props.fields.PromoImageTwo}
              PromoImageThree={props.fields.PromoImageThree}
              withShapes={withShapes}
              withShadows={withShadows}
            />
          )}
        </div>

        <div className={`col-span-full ${secondColumnSize} ${justifyContentClass}`}>
          <PromoContent {...props} />
        </div>
      </div>
    </section>
  );
};

export const WithFullImage = (props: PromoProps): JSX.Element => {
  const id = props.params.RenderingIdentifier;
  const isPromoReversed = !props?.params?.styles?.includes(LayoutStyles.Reversed)
    ? ' flex-col'
    : 'flex-col-reverse';

  return (
    <section className={`${props.params.styles} py-20`} id={id ? id : undefined}>
      <div className={`container flex ${isPromoReversed}`}>
        <div className="relative my-10 aspect-[1232/608] overflow-hidden rounded-2xl">
          <ContentSdkImage
            field={props.fields.PromoImageTwo}
            className="h-full w-full object-cover"
          />
        </div>

        <div className="space-y-5">
          <div className="text-foreground-light font-semibold uppercase">
            <Text field={props.fields.PromoSubTitle} />
          </div>

          <div className="grid-col-1 grid gap-5 md:grid-cols-2">
            <div className="font-bold">
              <h2 className="max-w-md">
                <Text field={props.fields.PromoTitle} />
              </h2>
            </div>

            <div className="flex max-w-md items-center">
              <ContentSdkRichText className="promo-text" field={props.fields.PromoDescription} />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export const WithQuote = (props: PromoProps): JSX.Element => {
  const id = props.params.RenderingIdentifier;
  const withQuote = !props?.params?.styles?.includes(PromoFlags.HidePromoQuotes);
  const isReversed = !props?.params?.styles?.includes(LayoutStyles.Reversed);

  const classesWhenReversed = {
    container: isReversed ? 'container-align-left' : 'container-align-right',
    contentOrder: isReversed ? 'order-1 lg:order-2' : 'order-2 lg:order-1',
    imageTransform: isReversed
      ? '-translate-x-[10%] xl:-translate-x-[20%]'
      : 'translate-x-[10%] xl:translate-x-[15%]',
    quoteFlip: isReversed ? '' : 'lg:-scale-x-100',
  };

  return (
    <section
      className={`relative ${props.params.styles} z-10 overflow-hidden pb-15 xl:pb-[4%]`}
      id={id ? id : undefined}
    >
      {withQuote && (
        <div
          className={`absolute left-5 md:top-[10%] lg:top-[25%] lg:left-1/2 lg:-translate-x-1/2 ${classesWhenReversed.quoteFlip} } text-background-accent! z-20`}
        >
          <Quote className="h-10 md:h-20 lg:h-25 xl:h-30" />
        </div>
      )}
      <div className="bg-background">
        <div className={`${classesWhenReversed.container} `}>
          <div className={`grid grid-cols-1 lg:grid-cols-3 lg:gap-0`}>
            <div
              className={`relative mt-10 flex items-center justify-center lg:col-span-1 ${classesWhenReversed.contentOrder}`}
            >
              <div className="text-foreground! mb-5 max-w-sm">
                <PromoContent {...props} />
              </div>
            </div>

            <div
              className={`relative z-30 order-2 mb-2 aspect-2/1 w-full translate-y-[25%] scale-100 place-self-end lg:order-1 lg:col-span-2 lg:h-3/4 xl:scale-90 ${classesWhenReversed.imageTransform}`}
            >
              <ContentSdkImage
                field={props.fields.PromoImageOne}
                className="absolute inset-0 h-full w-full rounded-2xl object-cover"
              />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

import { ComponentProps } from '@/lib/component-props';
import {
  ComponentParams,
  ComponentRendering,
  Text,
  TextField,
  useSitecore,
