/**
 * The number of rows that can be inserted into the row splitter component.
 * The maximum number of rows is 8.
 */
type RowNumber = 1 | 2 | 3 | 4 | 5 | 6 | 7 | 8;

/**
 * The styles specified for each rendered row.
 * The key is the row number, and the value is the styles.
 */
type RowStyles = {
  [K in `Styles${RowNumber}`]?: string;
};

interface RowSplitterProps extends ComponentProps {
  rendering: ComponentRendering;
  params: ComponentProps['params'] & RowStyles;
}

export const Default = ({ params, rendering }: RowSplitterProps): JSX.Element => {
  const enabledPlaceholders = params.EnabledPlaceholders?.split(',') ?? [];
  const id = params.RenderingIdentifier;

  return (
    <div className={`component row-splitter ${params.styles}`} id={id}>
      {enabledPlaceholders.map((ph, index) => {
        const num = Number(ph) as RowNumber;
        const placeholderKey = `row-${num}-{*}`;
        const rowStyles = `${params[`Styles${num}`] ?? ''}`.trimEnd();

        return (
          <div key={index} className={`container-fluid ${rowStyles}`.trimEnd()}>
            <div>
              <div className="row">
                <Placeholder name={placeholderKey} rendering={rendering} />
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

import { Link, LinkField, Text, TextField, useSitecore } from '@sitecore-content-sdk/nextjs';
import React, { JSX } from 'react';
import { ComponentProps } from 'lib/component-props';

interface Item {
  url: {
    path: string;
    siteName: string;
  };
  field: {
    jsonValue: {
      value: string;
    };
  };
}

interface TitleProps extends ComponentProps {
  fields: {
    /**
     * The Integrated graphQL query result. This illustrates the way to access the context item datasource information.
     */
    data?: {
      datasource?: Item;
      contextItem?: Item;
    };
  };
}

interface ComponentContentProps {
  id?: string;
  styles?: string;
  children: React.ReactNode;
}

const ComponentContent = ({ id, styles = '', children }: ComponentContentProps): JSX.Element => (
  <div className={`component title ${styles.trim()}`} id={id}>
    <div className="component-content">
      <h1 className="field-title">{children}</h1>
    </div>
  </div>
);

export const Default = ({ params, fields }: TitleProps): JSX.Element => {
  const { page } = useSitecore();
  const { styles, RenderingIdentifier: id } = params;
  const datasource = fields?.data?.datasource || fields?.data?.contextItem;
  const text: TextField = datasource?.field?.jsonValue || {};
  const link: LinkField = {
    value: {
      href: datasource?.url?.path,
      title: datasource?.field?.jsonValue?.value,
    },
  };

  return (
    <ComponentContent styles={styles} id={id}>
      {page.mode.isEditing ? (
        <Text field={text} />
      ) : (
        <Link field={link}>
          <Text field={text} />
        </Link>
      )}
    </ComponentContent>
  );
};

import { NavigationProps, NavItemFields } from '@/components/navigation/Navigation';
import React, { JSX } from 'react';
import { LinkField, Text } from '@sitecore-content-sdk/nextjs';

export const isNavLevel = (fields: NavItemFields, level: number): boolean => {
  return Array.isArray(fields.Styles) && fields.Styles.includes(`level${level}`);
};

export const isNavRootItem = (fields: NavItemFields): boolean => {
  const isFlatLevel =
    Array.isArray(fields.Styles) && fields.Styles.some((style) => style.startsWith('flat-level'));

  return isNavLevel(fields, 0) && !isFlatLevel;
};

export const getLinkContent = (fields: NavItemFields, logoSrc?: string): JSX.Element | string => {
  const isRootItem = isNavRootItem(fields);

  if (isRootItem && logoSrc) {
    const altText =
      fields.NavigationTitle?.value || fields.Title?.value || fields.DisplayName || '';
    return <img src={logoSrc} alt={String(altText)} className="h-auto w-36" />;
  }

  const textField = fields.NavigationTitle || fields.Title;
  if (textField) {
    return <Text field={textField} />;
  }

  return fields.DisplayName;
};

export const getLinkField = (fields: NavItemFields): LinkField => ({
  value: {
    href: fields.Href,
    title:
      fields.NavigationTitle?.value?.toString() ??
      fields.Title?.value?.toString() ??
      fields.DisplayName,
    querystring: fields.Querystring,
  },
});

export const prepareFields = (
  fields: NavigationProps['fields'],
  center: boolean = true
): NavigationProps['fields'] => {
  const result: NavigationProps['fields'] = {};
  const entries = Object.entries(fields).filter(Boolean);

  if (entries.length === 1 && isNavRootItem(entries[0][1])) {
    const rootItem = entries[0][1];
    const children = rootItem.Children || [];

    // root item always gets flattened (children merged into top-level)
    const flattenedChildren = [...children];
    if (center) {
      // place root item in the middle
      const middleIndex = Math.floor(children.length / 2);
      flattenedChildren.splice(middleIndex, 0, { ...rootItem, Children: undefined });
    } else {
      // place root item at the start (before children)
      flattenedChildren.unshift({ ...rootItem, Children: undefined });
    }

    flattenedChildren.forEach((item, idx) => {
      result[String(idx)] = item;
    });
  } else {
    entries.forEach(([key, item]) => {
      result[key] = item;
    });
  }

  return result;
};

import { useEffect } from 'react';

export const useClickAway = (ref: React.RefObject<HTMLElement | null>, onClickAway: () => void) => {
  useEffect(() => {
    function handleClick(event: MouseEvent) {
      if (!ref.current) return;
      if (!ref.current.contains(event.target as Node)) {
        onClickAway();
      }
    }

    document.addEventListener('mousedown', handleClick);
    document.addEventListener('touchstart', handleClick);

    return () => {
      document.removeEventListener('mousedown', handleClick);
      document.removeEventListener('touchstart', handleClick);
    };
  }, [ref, onClickAway]);
};

import { useEffect } from 'react';

export function useStopResponsiveTransition(delay = 300) {
  useEffect(() => {
    let timer: NodeJS.Timeout | null = null;
    const classes = document.body.classList;

    function handleResize() {
      if (timer) {
        clearTimeout(timer);
        timer = null;
      } else {
        classes.add('stop-transition');
      }

      timer = setTimeout(() => {
        classes.remove('stop-transition');
        timer = null;
      }, delay);
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [delay]);
}

