import { CdpHelper, useSitecore } from '@sitecore-content-sdk/nextjs';
import { useEffect } from 'react';
import { pageView } from '@sitecore-cloudsdk/events/browser';
import config from 'sitecore.config';
import { JSX } from 'react';

/**
 * This is the CDP page view component.
 * It uses the Sitecore Cloud SDK to enable page view events on the client-side.
 * See Sitecore Cloud SDK documentation for details.
 * https://www.npmjs.com/package/@sitecore-cloudsdk/events
 */
const CdpPageView = (): JSX.Element => {
  const {
    page: { layout, siteName, mode },
  } = useSitecore();
  const { route, context } = layout.sitecore;

  /**
   * Determines if the page view events should be turned off.
   * IMPORTANT: You should implement based on your cookie consent management solution of choice.
   * By default it is disabled in development mode
   */
  const disabled = () => {
    return process.env.NODE_ENV === 'development';
  };

  useEffect(() => {
    // Do not create events in editing or preview mode or if missing route data
    if (!mode.isNormal || !route?.itemId) {
      return;
    }
    // Do not create events if disabled (e.g. we don't have consent)
    if (disabled()) {
      return;
    }

    const language = route.itemLanguage || config.defaultLanguage;
    const scope = config.personalize?.scope;

    const pageVariantId = CdpHelper.getPageVariantId(
      route.itemId,
      language,
      context.variantId as string,
      scope
    );
    // there can be cases where Events are not initialized which are expected to reject
    pageView({
      channel: 'WEB',
      currency: 'USD',
      page: route.name,
      pageVariantId,
      language,
    }).catch((e) => console.debug(e));
  }, [mode, route, context.variantId, siteName]);

  return <></>;
};

export default CdpPageView;

import Image from 'next/image';
import * as FEAAS from '@sitecore-feaas/clientside/react';
import nextConfig from 'next.config';
import { JSX } from 'react';
// Element implementations for Sitecore Component Builder can be overriden here

const FEAASScripts = (): JSX.Element => {
  // we cannot use nextjs's logic for remotePatterns matching without extra dependencies
  // so we use a limited approach for now - which will be replaced once nextjs allows to fall back to unoptimized OOB
  const convertToRegex = (pattern: string) => {
    return pattern.replace('.', '\\.').replace(/\*/g, '.*');
  };

  const shouldOptimize = (src: string) => {
    if (src.startsWith('http')) {
      const url = new URL(src);
      const domains: string[] = nextConfig.images?.domains || [];
      const remotePatterns = nextConfig.images?.remotePatterns || [];
      return (
        domains.some((domain) => url.hostname === domain) ||
        remotePatterns.some(
          (pattern) =>
            pattern.protocol === url.protocol.slice(0, -1) &&
            new RegExp('^' + convertToRegex(pattern.hostname) + '$').test(url.hostname)
        )
      );
    }
    return true;
  };

  // Register next Image to be used in Component Builder.
  // Nextjs image implementation will be used when img is rendered in component from Component Builder
  FEAAS.setElementImplementation('img', (attributes: Record<string, string>) => {
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const { children, src, alt, ...imgAttributes } = attributes;
    return (
      <Image
        height={1920}
        width={1200}
        unoptimized={!shouldOptimize(src)}
        src={src}
        alt={alt}
        {...imgAttributes}
      />
    );
  });

  return <></>;
};

export default FEAASScripts;

import Head from 'next/head';
import client from 'lib/sitecore-client';
import { LayoutServiceData, HTMLLink } from '@sitecore-content-sdk/nextjs';

/**
 * Component to render `<link>` elements for Sitecore styles
 */
const SitecoreStyles = ({
  layoutData,
  enableStyles,
  enableThemes,
}: {
  layoutData: LayoutServiceData;
  enableStyles?: boolean;
  enableThemes?: boolean;
}) => {
  const headLinks = client.getHeadLinks(layoutData, { enableStyles, enableThemes });

  if (headLinks.length === 0) {
    return null;
  }

  return (
    <Head>
      {headLinks.map(({ rel, href }: HTMLLink) => (
        <link rel={rel} key={href} href={href} />
      ))}
    </Head>
  );
};

export default SitecoreStyles;

import { IGQLField, IGQLTextField } from '@/types/igql';
import {
  ComponentParams,
  ComponentRendering,
  Link,
  LinkField,
  Text,
  useSitecore,
} from '@sitecore-content-sdk/nextjs';
import React from 'react';

interface Fields {
  data: {
    datasource: {
      children: {
        results: Deal[];
      };
      title: IGQLTextField;
      description: IGQLTextField;
    };
  };
}

interface Deal {
  dealTitle: IGQLTextField;
  dealDescription: IGQLTextField;
  dealCtaLink: IGQLField<LinkField>;
  offerText: IGQLTextField;
  dealValidity: IGQLTextField;
}

type DealsProps = {
  rendering: ComponentRendering & { params: ComponentParams };
  params: { [key: string]: string };
  fields: Fields;
};

type DealWrapperProps = {
  props: DealsProps;
  children: React.ReactNode;
};

const DealWrapper = (wrapperProps: DealWrapperProps) => {
  const id = wrapperProps.props.params.RenderingIdentifier;
  return (
    <section className={`py-16 ${wrapperProps.props.params.styles}`} id={id ? id : undefined}>
      {wrapperProps.children}
    </section>
  );
};

export const Default = (props: DealsProps) => {
  const { page } = useSitecore();
  const isPageEditing = page.mode.isEditing;
  const results = props.fields.data.datasource.children.results;
  const mainTitle = props.fields.data.datasource.title;
  const mainDescription = props.fields.data.datasource.description;

  return (
    <DealWrapper props={props}>
      <div className="container mx-auto px-4">
        <div className="mb-12 text-center">
          <h2 className="mb-4 font-bold">
            <Text field={mainTitle.jsonValue} />
          </h2>
          <p className="text-foreground-light text-xl">
            <Text field={mainDescription.jsonValue} />
          </p>
        </div>

        <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
          {results.map((item, index) => {
            const title = item.dealTitle.jsonValue;
            const description = item.dealDescription.jsonValue;
            const link = item.dealCtaLink.jsonValue;
            const offer = item.offerText.jsonValue;
            const validity = item.dealValidity.jsonValue;
            const hasContent =
              title?.value ||
              description?.value ||
              link?.value?.href ||
              offer?.value ||
              validity?.value;

            return (
              (hasContent || isPageEditing) && (
                <div
                  key={index}
                  className="border-accent-light flex flex-col rounded-lg border-2 transition-colors"
                >
                  <div className="flex min-h-24 items-start justify-between gap-6 p-4">
                    <h3 className="flex-1 text-xl font-bold">
                      <Text field={title} />
                    </h3>

                    {(offer?.value || isPageEditing) && (
                      <span className="bg-danger text-background self-start rounded px-2 py-1 text-base font-semibold">
                        <Text field={offer} />
                      </span>
                    )}
                  </div>

                  <div className="flex flex-1 flex-col p-4 pt-0">
                    <p className="mb-4">
                      <Text field={description} />
                    </p>

                    <p className="text-accent mb-4 text-sm font-medium">
                      <Text field={validity} />
                    </p>

                    <div className="bg-foreground text-background mt-auto w-full rounded-md py-2 text-center transition hover:opacity-90">
                      <Link field={link} />
                    </div>
                  </div>
                </div>
              )
            );
          })}
        </div>
      </div>
    </DealWrapper>
  );
};

import { ComponentProps } from '@/lib/component-props';
import { NextImage as Image, Text, RichText, useSitecore } from '@sitecore-content-sdk/nextjs';
import Head from 'next/head';
