import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as Container } from '../components/container/Container';
import { ComponentProps } from 'react';
import { renderStorybookPlaceholder } from './helpers/renderStorybookPlaceholder';
import { CommonParams, CommonRendering } from './common/commonData';
import {
  BackgroundColorArgs,
  backgroundColorArgTypes,
  defaultBackgroundColorArgs,
  defaultHighlightArgs,
  defaultLayoutArgs,
  HighlightArgs,
  HighlightArgTypes,
  LayoutArgs,
  LayoutArgTypes,
} from './common/commonControls';
import { createRichTextField } from './helpers/createFields';

type StoryProps = ComponentProps<typeof Container> &
  BackgroundColorArgs &
  HighlightArgs &
  LayoutArgs & {
    boxed?: boolean;
    bordered?: boolean;
    centered?: boolean;
  };

const meta = {
  title: 'Page Structure/Container',
  component: Container,
  tags: ['autodocs'],
  argTypes: {
    ...backgroundColorArgTypes,
    ...HighlightArgTypes,
    ...LayoutArgTypes,
    boxed: {
      name: 'Boxed',
      control: { type: 'boolean' },
      defaultValue: false,
    },
    bordered: {
      name: 'Bordered',
      control: { type: 'boolean' },
      defaultValue: false,
    },
    centered: {
      name: 'Centered',
      control: { type: 'boolean' },
      defaultValue: false,
    },
  },
  args: {
    ...defaultBackgroundColorArgs,
    ...defaultHighlightArgs,
    ...defaultLayoutArgs,
    boxed: false,
    bordered: false,
    centered: false,
  },
} satisfies Meta<StoryProps>;
export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = {
  ...CommonParams,
};

const baseRendering = {
  ...CommonRendering,
  componentName: 'Container',
  params: baseParams,
};

export const Default: Story = {
  render: (args) => {
    return (
      <Container
        params={{
          ...baseParams,
          styles: `${baseParams.styles}
            ${args.boxed ? 'boxed' : ''}
            ${args.bordered ? 'sxa-bordered' : ''}
            ${args.centered ? 'container' : ''}
            ${args.BackgroundColor}
            ${args.Highlight.join(' ')}
            ${args.Indent.join(' ')}
            ${args.ContentAlignment.join(' ')}
          `,
        }}
        rendering={{
          ...baseRendering,
          placeholders: {
            [`container-${baseParams.DynamicPlaceholderId}`]: [renderStorybookPlaceholder()],
          },
        }}
      />
    );
  },
};

export const WithPlaceholderData: Story = {
  render: (args) => {
    return (
      <Container
        params={{
          ...baseParams,
          styles: `${baseParams.styles}
            ${args.boxed ? 'boxed' : ''}
            ${args.bordered ? 'sxa-bordered' : ''}
            ${args.centered ? 'container' : ''}
            ${args.BackgroundColor}
            ${args.Highlight.join(' ')}
            ${args.Indent.join(' ')}
            ${args.ContentAlignment.join(' ')}
          `,
        }}
        rendering={{
          ...baseRendering,
          placeholders: {
            [`container-${baseParams.DynamicPlaceholderId}`]: [
              {
                ...CommonRendering,
                componentName: 'RichText',
                params: CommonParams,
                fields: {
                  Text: createRichTextField(3),
                },
              },
            ],
          },
        }}
      />
    );
  },
};

import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default, Card } from '../components/features/Features';
import { ComponentProps } from 'react';
import { CommonParams, CommonRendering } from './common/commonData';
import {
  BackgroundColorArgs,
  backgroundColorArgTypes,
  defaultBackgroundColorArgs,
} from './common/commonControls';
import { createFeatureItems } from './helpers/createItems';
import { createIGQLData } from './helpers/createIGQLData';
import { createRichTextField, createTextField, createIGQLField } from './helpers/createFields';
import clsx from 'clsx';

type StoryProps = ComponentProps<typeof Default> &
  BackgroundColorArgs & {
    numberOfItems: number;
  };

type FieldsType = ComponentProps<typeof Default>['fields'];

const meta = {
  title: 'Page Content/Features',
  component: Default,
  tags: ['autodocs'],
  argTypes: {
    ...backgroundColorArgTypes,
    numberOfItems: {
      name: 'Number of features',
      control: {
        type: 'range',
        min: 1,
        max: 21,
        step: 1,
      },
    },
  },
  args: {
    numberOfItems: 3,
    ...defaultBackgroundColorArgs,
  },
} satisfies Meta<StoryProps>;
export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = CommonParams;

const baseRendering = {
  ...CommonRendering,
  componentName: 'Features',
  params: CommonParams,
};

export const DefaultFeatures: Story = {
  render: (args) => {
    const featureStyles = clsx(baseParams.styles, args.BackgroundColor);
    const params = {
      ...baseParams,
      styles: featureStyles,
    };

    return (
      <Default
        fields={
          createIGQLData({
            createItems: createFeatureItems,
            count: args.numberOfItems,
            topLevelFields: {
              title: createIGQLField(createTextField('Tips for Energy Conservation')),
              description: createIGQLField(createRichTextField(1)),
            },
          }) as unknown as FieldsType
        }
        params={params}
        rendering={baseRendering}
      />
    );
  },
};

export const CardFeatures: Story = {
  parameters: {
    layout: 'padded',
  },
  render: (args) => {
    const featureStyles = clsx(baseParams.styles, args.BackgroundColor);
    const params = {
      ...baseParams,
      styles: featureStyles,
    };

    return (
      <Card
        fields={
          createIGQLData({
            createItems: createFeatureItems,
            count: args.numberOfItems,
            topLevelFields: {
              title: createIGQLField(createTextField('Quick Actions')),
              description: createIGQLField(createRichTextField(1)),
            },
          }) as unknown as FieldsType
        }
        params={params}
        rendering={baseRendering}
      />
    );
  },
};

import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as Footer } from '../components/footer/Footer';
import { ComponentProps } from 'react';
import { renderStorybookPlaceholder } from './helpers/renderStorybookPlaceholder';
import { CommonParams, CommonRendering } from './common/commonData';
import {
  createImageField,
  createLinkField,
  createRichTextField,
  createTextField,
} from './helpers/createFields';
import { createIGQLData } from './helpers/createIGQLData';
import { createLinkItems } from './helpers/createItems';
import { ComponentFields } from '@sitecore-content-sdk/nextjs';
import clsx from 'clsx';

type StoryProps = ComponentProps<typeof Footer> & {
  hideTopSection?: boolean;
  hideBottomSection?: boolean;
};

const meta = {
  title: 'Global Components/Footer',
  component: Footer,
  tags: ['autodocs'],
} satisfies Meta<
  StoryProps & {
    hideTopSection: boolean;
    hideBottomSection: boolean;
  }
>;

export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = {
  ...CommonParams,
};

const baseRendering = {
  ...CommonRendering,
  componentName: 'Container',
  params: baseParams,
};

const baseFields = {
  TitleOne: createTextField('About Gridwell'),
  TitleTwo: createTextField('Contact'),
  TitleThree: createTextField('Quick Links'),
  CopyrightText: createTextField('Â© 2025 Gridwell. All rights reserved.'),
  PolicyText: createLinkField('Privacy'),
  TermsText: createLinkField('Terms'),
  CookiesText: createLinkField('Cookies'),
  ContactText: createLinkField('Contact Us'),
  Description: createRichTextField(1),
  Logo: createImageField('logo'),
  LogoDark: createImageField('logo'),
};

const SocialFollowRendering = {
  ...CommonRendering,
  componentName: 'SocialFollow',
  params: CommonParams,
  fields: {
    FacebookLink: createLinkField('Facebook'),
    InstagramLink: createLinkField('Instagram'),
    TwitterLink: createLinkField('Twitter'),
    YoutubeLink: createLinkField('Youtube'),
  } as unknown as ComponentFields,
};

const LinkListRendering = {
  ...CommonRendering,
  componentName: 'LinkList',
  params: {
    ...CommonParams,
    Styles: 'list-vertical',
  },
  fields: createIGQLData({
    count: 3,
    topLevelFields: {},
    createItems: createLinkItems,
  }) as unknown as ComponentFields,
};

export const Default: Story = {
  args: {
    hideTopSection: false,
    hideBottomSection: false,
  },

  render: () => {
    const styles = clsx(baseParams.Styles);

    const params = {
      ...baseParams,
      Styles: styles,
    };

    return (
      <Footer
        params={params}
        rendering={{
          ...baseRendering,
          params,
          placeholders: {
            [`footer-list-first-${baseParams.DynamicPlaceholderId}`]: [
              renderStorybookPlaceholder(),
            ],
            [`footer-list-second-${baseParams.DynamicPlaceholderId}`]: [
              renderStorybookPlaceholder(),
            ],
            [`footer-list-third-${baseParams.DynamicPlaceholderId}`]: [
              renderStorybookPlaceholder(),
            ],
            [`footer-list-fourth-${baseParams.DynamicPlaceholderId}`]: [
              renderStorybookPlaceholder(),
            ],
          },
        }}
        fields={baseFields}
      />
    );
  },
};

export const WithPlaceholderData: Story = {
  args: {
    hideTopSection: false,
    hideBottomSection: false,
  },

  render: () => {
    const styles = clsx(baseParams.Styles);

    const params = {
      ...baseParams,
      Styles: styles,
    };

    return (
      <Footer
        params={params}
        rendering={{
          ...baseRendering,
          params,
          placeholders: {
            [`footer-list-first-${baseParams.DynamicPlaceholderId}`]: [LinkListRendering],
            [`footer-list-second-${baseParams.DynamicPlaceholderId}`]: [LinkListRendering],
            [`footer-list-third-${baseParams.DynamicPlaceholderId}`]: [LinkListRendering],
            [`footer-list-fourth-${baseParams.DynamicPlaceholderId}`]: [SocialFollowRendering],
          },
        }}
        fields={baseFields}
      />
    );
  },
};

import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as GridConditions } from '../components/grid-conditions/GridConditions';
import { ComponentProps } from 'react';
import { CommonParams, CommonRendering } from './common/commonData';
import { boolToSitecoreCheckbox } from './helpers/boolToSitecoreCheckbox';

type StoryProps = ComponentProps<typeof GridConditions> & {
  HideGridSection: boolean;
  HideTemperatureSection: boolean;
  HideOutagesSection: boolean;
};

const meta = {
  title: 'Grid/Grid Conditions',
  component: GridConditions,
  tags: ['autodocs'],
  argTypes: {
    HideGridSection: {
      control: 'boolean',
      name: 'Hide Grid Section',
    },
    HideTemperatureSection: {
      control: 'boolean',
      name: 'Hide Temperature Section',
    },
    HideOutagesSection: {
      control: 'boolean',
      name: 'Hide Outages Section',
    },
  },
  args: {
    HideGridSection: false,
    HideTemperatureSection: false,
    HideOutagesSection: false,
  },
} satisfies Meta<StoryProps>;
export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = {
  ...CommonParams,
};

const baseRendering = {
  ...CommonRendering,
  componentName: 'GridConditions',
  params: baseParams,
};

export const Default: Story = {
  render: (args) => {
    const params = {
      ...baseParams,
      HideGridSection: boolToSitecoreCheckbox(args.HideGridSection),
      HideTemperatureSection: boolToSitecoreCheckbox(args.HideTemperatureSection),
      HideOutagesSection: boolToSitecoreCheckbox(args.HideOutagesSection),
    };
    return <GridConditions params={params} rendering={baseRendering} />;
  },
};

