import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as Header } from '../components/header/Header';
import { ComponentProps } from 'react';
import { CommonParams, CommonRendering } from './common/commonData';
import { renderStorybookPlaceholder } from './helpers/renderStorybookPlaceholder';
import { getNavigationFields, logoParam } from './constants/navFields';
import { ComponentFields } from '@sitecore-content-sdk/nextjs';
import { createImageField, createLinkField } from './helpers/createFields';

type StoryProps = ComponentProps<typeof Header> & {
  withRoot?: boolean;
  isFlat?: boolean;
  hasLogo?: boolean;
  isSimpleLayout?: boolean;
};

const meta = {
  title: 'Global Elements/Header',
  component: Header,
  tags: ['autodocs'],
} satisfies Meta<StoryProps>;
export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = {
  ...CommonParams,
};

const baseRendering = {
  ...CommonRendering,
  componentName: 'Header',
  params: baseParams,
};

export const Default: Story = {
  render: () => {
    return (
      <Header
        params={baseParams}
        rendering={{
          ...baseRendering,
          placeholders: {
            [`header-left-${baseParams.DynamicPlaceholderId}`]: [renderStorybookPlaceholder()],
            [`header-nav-${baseParams.DynamicPlaceholderId}`]: [renderStorybookPlaceholder()],
            [`header-right-${baseParams.DynamicPlaceholderId}`]: [renderStorybookPlaceholder()],
          },
        }}
      />
    );
  },
};

export const WithPlaceholderData: Story = {
  render: () => {
    return (
      <Header
        params={baseParams}
        rendering={{
          ...baseRendering,
          placeholders: {
            [`header-left-${baseParams.DynamicPlaceholderId}`]: [
              {
                ...CommonRendering,
                componentName: 'Image',
                params: CommonParams,
                fields: {
                  Image: createImageField('logo'),
                  TargetUrl: createLinkField(),
                },
              },
            ],
            [`header-nav-${baseParams.DynamicPlaceholderId}`]: [
              {
                ...CommonRendering,
                componentName: 'Navigation',
                params: { ...CommonParams, Logo: logoParam },
                fields: getNavigationFields({ withRoot: false }) as unknown as ComponentFields,
              },
            ],
            [`header-right-${baseParams.DynamicPlaceholderId}`]: [
              {
                ...CommonRendering,
                componentName: 'LinkList',
                params: { ...CommonParams },
                fields: {
                  data: {
                    datasource: {
                      children: {
                        results: [
                          { field: { link: createLinkField('Check In') } },
                          { field: { link: createLinkField('Search') } },
                        ],
                      },
                    },
                  },
                } as unknown as ComponentFields,
              },
            ],
          },
        }}
      />
    );
  },
};

import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { ComponentProps } from 'react';
import { Default as HeroBanner } from '../components/hero-banner/HeroBanner';
