import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as Image } from '../components/image/Image';
import { ComponentProps } from 'react';
import { CommonParams, CommonRendering } from './common/commonData';
import { createImageField, createTextField, createLinkField } from './helpers/createFields';

type StoryProps = ComponentProps<typeof Image> & {
  caption?: string;
  linkText?: string;
  hasLink?: boolean;
};

const meta = {
  title: 'Media/Image',
  component: Image,
  tags: ['autodocs'],
  parameters: {
    layout: 'centered',
  },
  argTypes: {
    caption: {
      control: 'text',
      description: 'Caption text for the image',
    },
    linkText: {
      control: 'text',
      description: 'Tooltip text when hovering over the image (when hasLink is true)',
    },
    hasLink: {
      control: 'boolean',
      description: 'Whether the image should have a link',
    },
  },
} satisfies Meta<StoryProps>;
export default meta;

type Story = StoryObj<StoryProps>;

const baseParams = {
  ...CommonParams,
};

const baseRendering = {
  ...CommonRendering,
  componentName: 'Image',
  params: baseParams,
};

export const Default: Story = {
  args: {
    caption: 'Image Caption',
    hasLink: false,
    linkText: 'Link Text',
  },
  render: ({ caption, hasLink, linkText }) => {
    return (
      <Image
        params={baseParams}
        rendering={baseRendering}
        fields={{
          Image: createImageField('placeholder'),
          ImageCaption: caption
            ? createTextField(caption, 1)
            : {
                value: '',
              },
          TargetUrl: hasLink
            ? createLinkField(linkText)
            : {
                value: {},
              },
        }}
      />
    );
  },
};

import type { Meta, StoryObj } from '@storybook/nextjs-vite';
import { Default as LinkList } from '../components/link-list/LinkList';
import { ComponentProps } from 'react';
import { CommonParams, CommonRendering } from './common/commonData';
import { createTextField } from './helpers/createFields';
import { createIGQLData } from './helpers/createIGQLData';
import { createLinkItems } from './helpers/createItems';

type StoryProps = ComponentProps<typeof LinkList> & {
  numberOfItems: number;
  vertical?: boolean;
};

const meta = {
  title: 'Navigation/LinkList',
  component: LinkList,
  tags: ['autodocs'],
  parameters: {
    layout: 'padded',
  },
  argTypes: {
    numberOfItems: {
      name: 'Number of links',
      control: {
        type: 'range',
        min: 1,
        max: 21,
        step: 1,
      },
    },
    vertical: {
      name: 'Vertical List',
      control: { type: 'boolean' },
      defaultValue: false,
    },
  },
  args: {
    numberOfItems: 3,
    vertical: false,
  },
} satisfies Meta<StoryProps>;
