  return {
    value: {
      src,
      alt: type,
      width,
      height,
    },
  };
};

export const createLinkField = (text: string = 'Read more') => {
  return {
    value: {
      linktype: '',
      id: '',
      anchor: '',
      querystring: '',
      target: '',
      class: '',
      text,
      title: text,
      href: '#',
    },
  };
};

export const createRichTextField = (
  numOfParagraphs: number = 2,
  variant: 'paragraphs' | 'withRichMarkup' = 'paragraphs'
) => {
  if (variant === 'withRichMarkup') {
    return {
      value: `<div class="ck-content">${RICH_MARKUP_INNER_HTML}</div>`,
    };
  }

  const paragraphs = loremIpsum({
    units: 'paragraphs',
    count: numOfParagraphs,
    format: 'html',
    paragraphLowerBound: 1,
    paragraphUpperBound: 4,
  });

  return {
    value: `<div class="ck-content">${paragraphs}</div>`,
  };
};

export const createTextField = (text?: string, numOfSentences: number = 3) => {
  const sentences = loremIpsum({
    units: 'sentences',
    count: numOfSentences,
  });

  return {
    value: text || sentences,
  };
};

export const createNumberField = (value?: number) => {
  return {
    value: value ?? 1234,
  };
};

export const createIGQLField = <T>(field: T): { jsonValue: T } => ({
  jsonValue: field,
});

export type IGQLField<T> = { jsonValue: T };

interface CreateIGQLDataType<ResultsType, TopLevelFields extends Record<string, unknown>> {
  createItems: (count: number) => ResultsType;
  count: number;
  topLevelFields: TopLevelFields;
}

export const createIGQLData = <ResultsType, TopLevelFields extends Record<string, unknown>>({
  createItems,
  count,
  topLevelFields,
}: CreateIGQLDataType<ResultsType, TopLevelFields>) => {
  return {
    data: {
      datasource: {
        children: {
          results: createItems(count),
        },
        ...topLevelFields,
      },
    },
  };
};

export const createSearchQueryData = <ResultsType, TopLevelFields extends Record<string, unknown>>({
  createItems,
  count,
  topLevelFields,
}: CreateIGQLDataType<ResultsType, TopLevelFields>) => {
  return {
    data: {
      search: {
        results: createItems(count),
        ...topLevelFields,
      },
    },
  };
};

