export const getNavigationFields = (options?: {
  withRoot?: boolean;
  flat?: boolean;
}): Record<string, NavItemFields> => {
  const { withRoot = true, flat = false } = options || {};

  const pages = flat ? flatTopLevelPages : topLevelPages;

  if (withRoot) {
    return {
      '0': {
        ...navRoot,
        Children: pages,
      },
    };
  }

  return arrayToObject(pages);
};

