import { ReviewFields } from '@/types/review';
import { Field } from '@sitecore-content-sdk/nextjs';
import { IGQLField } from '@/types/igql';

/**
 * Calculates the average rating from a list of reviews
 * @param reviews - Array of review objects with rating fields
 * @returns Average rating rounded to 1 decimal places, or 0 if no reviews
 */
export const calculateAverageRating = (reviews: ReviewFields[]): number => {
  if (!reviews || reviews.length === 0) return 0;

  const totalRating = reviews.reduce((sum, review) => sum + (review.fields.Rating?.value || 0), 0);
  return parseFloat((totalRating / reviews.length).toFixed(1));
};

/**
 * Calculates the average rating from IGQL review format (used in ProductListing)
 * @param reviews - Array of IGQL review objects with rating fields
 * @returns Average rating rounded to 1 decimal places, or 0 if no reviews
 */
export const calculateAverageRatingFromIGQL = (
  reviews: Array<{ rating: IGQLField<Field<number>> }>
): number => {
  if (!reviews || reviews.length === 0) return 0;

  const totalRating = reviews.reduce(
    (sum, review) => sum + (review.rating?.jsonValue?.value || 0),
    0
  );
  return parseFloat((totalRating / reviews.length).toFixed(1));
};

export const resolveBackgroundClassFromStyles = (styles: string = ''): string => {
  const styleToClassMap: Record<string, string> = {
    'container-white-background': 'bg-background',
    'container-gold-background': 'bg-background-accent',
    'container-gray-background': 'bg-background-muted',
  };
  const priority = Object.keys(styleToClassMap);
  const matchedStyle = priority.find((style) => styles.includes(style));
  return styleToClassMap[matchedStyle!] || 'bg-background'; // default
};

import { localeOptions } from '@/constants/localeOptions';
import { useSitecore } from '@sitecore-content-sdk/nextjs';
import { LocaleOption } from '@/types/locale';

/**
 * Get locale options for a given locale (defaults to current locale)
 */
export function useLocale(locale?: string): LocaleOption {
  const { page } = useSitecore();
  const currentLocale = page.locale || 'en';
  const targetLocale = locale || currentLocale;
  const localeItem = localeOptions.find((l) => l.code === targetLocale);
  return localeItem || localeOptions[0];
}

import { useMemo } from 'react';

interface UsePaginationProps {
  totalItems: number;
  currentPage: number;
  itemsPerPage: number;
  windowSize?: number; // number of page numbers to show in the middle
}

export function usePagination({
  totalItems,
  currentPage,
  itemsPerPage,
  windowSize = 3,
}: UsePaginationProps) {
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  const { startPage, endPage, visiblePages } = useMemo(() => {
    let start = Math.max(1, currentPage - Math.floor(windowSize / 2));
    let end = start + windowSize - 1;

    if (end > totalPages) {
      end = totalPages;
      start = Math.max(1, end - windowSize + 1);
    }

    const pages = Array.from({ length: end - start + 1 }, (_, i) => start + i);

    return { startPage: start, endPage: end, visiblePages: pages };
  }, [currentPage, totalPages, windowSize]);

  const getPageSlice = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return [startIndex, endIndex];
  };

  return { totalPages, startPage, endPage, visiblePages, getPageSlice };
}

// useSearchTracking.ts
import { useCallback } from 'react';
import { useRouter } from 'next/navigation';
import {
  trackEntityPageViewEvent,
  trackPreviewSearchClickEvent,
  trackSearchClickEntityEvent,
} from '@sitecore-search/react';

export type Events =
  | 'PageViewEvent'
  | 'EntityPageView'
  | 'PreviewSearchClickEvent'
  | 'SearchClickEvent'
  | 'PreviewSearchSuggestionClickEvent';

type Args = {
  url: string;
  widgetId: string;
  entityType: string;
  events: Events[];
  entityId: string;
  itemIndex: number;
};

export function useSearchTracking() {
  const router = useRouter();

  const handleSearch = useCallback(
    (e: React.MouseEvent<HTMLAnchorElement>, args: Args) => {
      const { url, widgetId, entityType, events, entityId, itemIndex } = args;

      e.preventDefault();

      events?.forEach((event) => {
        if (event === 'EntityPageView' && entityType && entityId) {
          trackEntityPageViewEvent(entityType, { items: [{ id: entityId }] });
        }
        if (event === 'PreviewSearchClickEvent') {
          trackPreviewSearchClickEvent(widgetId, entityType, {
            index: itemIndex,
            items: [{ id: entityId }],
          });
        }
        if (event === 'SearchClickEvent') {
          trackSearchClickEntityEvent(widgetId, entityType, {
            actionCause: 'entity',
            index: itemIndex,
            items: [{ id: entityId }],
          });
        }
      });

      router.push(url);
    },
    [router]
  );

  return { handleSearch };
}

import { Color, Product, Size } from '@/types/products';

export type CartProduct = Pick<Product, 'Title' | 'Price' | 'Image1'>;

export type CartItem = {
  id: string; // cart item ID
  product: CartProduct;
  quantity: number;
  color?: Color;
  size?: Size;
};

const CART_KEY = 'sitecore-demo-cart';

export function generateCartItemId(productId: string, color?: Color, size?: Size): string {
  const colorPart = color ? `_${color.id}` : '';
  const sizePart = size ? `_${size.id}` : '';
  return `${productId}${colorPart}${sizePart}`;
}

export function getCart(): CartItem[] {
  if (typeof window === 'undefined') return [];

  try {
    const raw = localStorage.getItem(CART_KEY);
    return raw ? JSON.parse(raw) : [];
  } catch (err) {
    console.warn('Failed to read cart from localStorage:', err);
    return [];
  }
}

export function saveCart(cart: CartItem[]) {
  if (typeof window === 'undefined') return;

  try {
    localStorage.setItem(CART_KEY, JSON.stringify(cart));
  } catch (err) {
    console.warn('Failed to save cart to localStorage:', err);
  }
}

export function addToCart(
  productId: string,
  product: Product,
  quantity: number = 1,
  color?: Color,
  size?: Size
): boolean {
  try {
    const cart = getCart();
    const uniqueId = generateCartItemId(productId, color, size);

    const idx = cart.findIndex((item) => item.id === uniqueId);

    const cartProduct: CartProduct = {
      Title: product.Title,
      Price: product.Price,
      Image1: product.Image1,
    };

    if (idx > -1) {
      cart[idx].quantity += quantity;
    } else {
      cart.push({
        id: uniqueId,
        product: cartProduct,
        quantity,
        color,
        size,
      });
    }

    saveCart(cart);
    return true;
  } catch (err) {
    console.error('Failed to add item to cart:', err);
    return false;
  }
}

export function removeFromCart(cartItemId: string): boolean {
  try {
    const cart = getCart().filter((item) => item.id !== cartItemId);
    saveCart(cart);
    return true;
  } catch (err) {
    console.error('Failed to remove item from cart:', err);
    return false;
  }
}

export function updateCartQuantity(cartItemId: string, quantity: number) {
  try {
    const cart = getCart();
    const idx = cart.findIndex((item) => item.id === cartItemId);
    if (idx > -1) {
      cart[idx].quantity = quantity;
      saveCart(cart);
    }
  } catch (err) {
    console.error('Failed to update cart quantity:', err);
  }
}

