export const createImageField = (type: 'logo' | 'placeholder' = 'placeholder') => {
  const imageTypeProps = {
    logo: {
      width: '220',
      height: '40',
    },
    placeholder: {
      width: '800',
      height: '800',
    },
  };

  const { width, height } = imageTypeProps[type];

  const src = createPlaceholderImageSrc(type === 'logo');

  return {
    value: {
      src,
      alt: type,
      width,
      height,
    },
  };
};

